INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO ) VALUES ("0102", "Oficiais das Forças Armadas", "Estão compreendidas nesta família ocupacional as pessoas que servem voluntariamente às forças armadas e que, por determinação legal, dedicam-se exclusivamente a esse serviço, não podendo aceitar emprego civil, exceto os cargos civis de natureza militar previstos em legislação específica. Suas funções consistem em manter, cumprir e defender a constituição federal, dentro da sua esfera de atribuições; observar as leis; promover as condições de segurança necessárias para o desenvolvimento e o bem-e", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0103", "Praças das Forças Armadas", "Estão compreendidas nesta família ocupacional as pessoas que servem voluntária ou obrigatoriamente às forças armadas e que, por determinação legal, dedicam-se exclusivamente a esse serviço, não podendo aceitar emprego civil, exceto os cargos civis de natureza militar previstos em legislação específica. Abrange os membros permanentes das forças armadas e o pessoal que se acha temporariamente em serviço ativo, por período estabelecido em leis ou regulamentos específicos, incorporados às as forças ", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0201", "Oficiais superiores da Polícia Militar", "Comandam unidades de polícia militar e elaboram plano diretor da instituição. Planejam ações estratégicas, definem ações táticas e executam ações operacionais. Gerenciam atividades administrativas, administram recursos humanos e mantêm hierarquia e disciplina.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0202", "Capitães da Polícia Militar", "Comandam operações de polícia ostensiva, planejam ações de policiamento ostensivo, desenvolvem policiamento comunitário. Gerenciam companhia de polícia e assessoram comando. Exercem poder disciplinar e presidem feitos de polícia judiciária militar.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0203", "Tenentes da Polícia Militar", "Comandam pelotão, coordenam policiamento ostensivo, reservado e velado; assessoram comando, gerenciam recursos humanos e logísticos, participam do planejamento de ações e operações, desenvolvem processos e procedimentos administrativos militares, atuam na coordenação da comunicação social;promovem estudos técnicos e capacitação profissional.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0211", "Subtenentes e Sargentos da Policia Militar", "Comandam o grupo e a guarda do quartel. Assessoram o comando, administram as atividades da unidade e participam na formação de policiais. Supervisionam policiamento ostensivo e organizam processos e procedimentos administrativo-militares. Atendem a ocorrências e as apresentam à autoridade competente. Prestam serviços comunitários.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0212", "Cabos e soldados da Polícia Militar", "Realizam policiamento ostensivo preventivo fardado e atendem e solucionam ocorrências. Executam atividades operacionais e policiamento reservado. Restabelecem ordem pública, controlam distúrbios civis e garantem cumprimento de mandado judicial.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0301", "Oficiais superiores do Corpo de Bombeiros Militar", "Definem estratégias e comandam unidades operacionais e administrativas do corpo de bombeiros, destinadas à proteção da vida e patrimônio do cidadão e ao meio ambiente. Gerenciam pessoal e recursos financeiros; controlam logística e dirigem serviços de inteligência e de prevenção, de acordo com normas da instituição.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0302", "Oficiais intermediários do Corpo de Bombeiros Militar", "Desenvolvem atividades administrativas em nível tático e operacional de bombeiro militar. Exercem funções de assessoramento e de comando de companhias de bombeiro militar, em espaço geográfico atribuído a um grupamento de bombeiros. Para tanto, administram recursos humanos, materiais e financeiros; comandam atividades de prevenção de sinistros; planejam atividades operacionais e administrativas. Administram e coordenam atividades de emergências e de combate a incêndios. Estabelecem parcerias com", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0303", "Tenentes do Corpo De Bombeiros Militar", "Comandam pelotões e postos de bombeiros; coordenam serviços operacionais; atuam na prevenção e normatização de sistemas e equipamentos de prevenção e combate a incêndio. Planejam atividades operacionais e administrativas e administram recursos humanos, financeiros e logísticos. Desenvolvem atividades de ensino e instrução e cuidam das informações e da comunicação social do corpo de bombeiros.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0311", "Subtenentes e Sargentos do Corpo de Bombeiros Militar", "Previnem sinistros e acidentes; realizam salvamento, combatem incêndios e prestam atendimento pré-hospitalar. Controlam acidentes com produtos perigosos, comandam equipes de serviços de prontidão e chefiam guarnições. No desenvolvimento das atividades, a rapidez e a eficácia da comunicação são cruciais, tanto para atender ocorrências como para tranqüilizar vítimas e orientar a população.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("0312", "Cabos e soldados do Corpo de Bombeiros Militar", "Realizam resgates e salvamentos; combatem incêndios;previnem acidentes e sinistros; preparam-se para ocorrências. Atendem ocorrências com produtos perigosos. Trabalham conforme normas e procedimentos técnicos, de segurança e preservação do meio ambiente. Estabelecem comunicação, triando e transmitindo informações, transmitindo e recebendo mensagens.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1111", "Legisladores", "Estudam as matérias previstas nas constituições federal e estaduais e deliberam sobre as mesmas; elaboram regimentos e discutem e votam o orçamento; dispoem sobre diversas matérias de competência da união, estados e municípios, como: tributos, arrecadação, distribuição de rendas, orçamentos, créditos, dívidas públicas, planos de desenvolvimento, criação de cargos públicos, limites territoriais e organização administrativa e judiciária; dispoem sobre matérias de interesse nacional, bem como trata", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1112", "Dirigentes gerais da administração pública", "Dirigem e administram um governo nacional, estadual e distrital ou municipal, de um ministério ou órgão assemelhado, fixando políticas globais e setoriais, acompanhando a execução das mesmas e avaliando seus resultados, para assegurar o bem-estar geral, a integridade e segurança do país e a defesa das instituições.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1113", "Magistrados", "Decidem conflitos individuais e coletivos em nome do estado, aplicando o direito a casos concretos, para isso, dirigem sessões e audiências judiciais, estabelecem critérios para a promoção da igualdade das partes, avaliam necessidade de provas para um julgamento seguro, decretam condenações ou absolvições em processos criminais, entre outras coisas; conciliam interesses, ouvindo e convocando as partes e propondo alternativas de acordo; efetivam o cumprimento das decisões; homologam situações não", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1114", "Dirigentes do serviço público", "Garantem suporte na gestão de pessoas, na administração de material, patrimônio, informática e serviços para as áreas meios e finalísticas da administração pública federal, estadual, distrital e municipal. Definem diretrizes, planejam,coordenam e supervisionam ações, monitorando resultados e fomentando políticas de mudança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1115", "Gestores Públicos", "Formulam, implementam e avaliam políticas públicas, supervisionam e executam atividades de planejamento e orçamento governamental de qualquer instituiçao da administraçao federal.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1130", "Dirigentes de povos indígenas, de quilombolas e caiçaras", "Organizam coletivamente as comunidades indígenas, quilombolas e caiçaras; preservam usos, costumes e artes da aldeia e da comunidade. Cobram providências para demarcação e manejo da área indígena, quilombola e caiçara. Articulam questões políticas; reivindicam melhorias para educação e saúde; buscam recursos em órgãos competentes. Providenciam e solicitam documentação. Realizam a manutenção e a preservação da medicina tradicional. Cobram a implantação de gestão dos recursos naturais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1141", "Dirigentes de partidos políticos", "Desenvolvem ações político-partidárias; promovem eventos políticos; estruturam o partido; coordenam campanhas eleitorais; administram recursos financeiros e gerenciam recursos humanos e materiais; atendem à legislação vigente e comunicam-se oralmentee por escrito, divulgando informações, ações e eventos do partido, elaborando relatórios, materiais de divulgação, manuais de orientação, entre outros.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1142", "Dirigentes e administradores de entidades patronais e dos trabalhadores e de outros interesses sócio", "Defendem interesses e identificam demandas de associados e representados; administram e representam entidades; coordenam assistência a associados e representados;propoem políticas de atuação e mobilizam associados e representados.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1143", "Dirigentes e administradores de entidades religiosas", "Implementam a missão religiosa; representam, jurídica e legalmente, a instituição; gerenciam operações administrativas e financeiras; coordenam atividades; articulam recursos humanos e materiais; comunicam-se oralmente e por escrito.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1144", "Dirigentes e administradores de organizações da sociedade civil sem fins lucrativos", "Promovem a sustentabilidade institucional; articulam alianças; representam a instituição e planejam políticas e estratégias. Comunicam-se, oralmente e por escrito; desenvolvem políticas e estratégias de recursos humanos; gerenciam operações administrativas e financeiras e coordenam atividades.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1210", "Diretores gerais", "Os diretores gerais, no mais alto nível da empresa, asseguram cumprimento da missão na empresa; estabelecem estratégias operacionais; determinam política de recursos humanos; coordenam diretorias e supervisionam negócios da empresa. Negociam transferência de tecnologia; representam e preservam a imagem da empresa; comunicam-se por meio de reuniões com os demais diretores, concedem entrevistas e participam de negociações.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1221", "Diretores de produção e operações em empresa agropecuária, pesqueira, aqüícola e florestal", "Dirigem as atividades de produção e operação em empresas como representantes dos proprietários ou acionistas ou por conta própria e, para tanto, definem políticas de gestão da empresa; estabelecem planejamento estratégico; avaliam desempenho da produção; traçam estratégias comerciais; administram os recursos da empresa; participam de negociações e promovem pesquisa e desenvolvimento.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1222", "Diretores de produção e operações em empresa da indústria extrativa, transformação e de serviços de ", "Planejam e administram a produção; atuam na definição do planejamento estratégico; participam na definição de políticas de rh; gerenciam logística; asseguram ações de gestão ambiental; gerenciam ações de qualidade; provêm condições de higiene e segurança do trabalho; comunicam-se oralmente e por escrito.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1223", "Diretores de operações de obras em empresa de construção", "Supervisionam contratos de obras, dirigem, como representantes dos proprietários, acionistas ou por conta própria, as atividades de desenvolvimento e operações de negócios de empresas de construção; participam do planejamento estratégico da empresa, planejam atividades técnicas e definem tecnologias de construção; administram recursos humanos,analisam viabilidade técnica e econômica de contratos e empreendimentos e gerenciam atendimento pós-venda.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1224", "Diretores de operações em empresa do comércio", "Estabelecem diretrizes e operações do comércio; coordenam recursos humanos e administram recursos materiais e financeiros. Dirigem operações do comércio; participam de atividades de marketing; implantam filiais e representações comerciais; comunicam-se em seminários, palestras, entrevistas e em contatos e negociações comerciais com clientes e distribuidores.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1225", "Diretores de operações de serviços em empresa de turismo, de alojamento e de alimentação", "Dirigem, no mais alto nível, e como representantes dos proprietários ou acionistas ou por conta própria, as atividades de produção e operação de empresas de prestação de serviços em turismo, alimentação e hotelaria. Definem política e diretrizes; traçam plano operacional; operacionalizam negócios; produzem resultados; coordenam equipes; garantem qualidade de produtos e serviços; analisam mercado e atuam como relações públicas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1226", "Diretores de operações de serviços em empresa de armazenamento, de transporte e de telecomunicação", "Viabilizam execução de metas operacionais em empresas de armazenamento, transportes e telecomunicações; organizam operações e controlam a execução de serviços; executam programas e normas; participam do planejamento operacional; coordenam atividades gerenciais e atuam como representantes legais da empresa.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1227", "Diretores de operações de serviços em instituição de intermediação financeira", "Dirigem as atividades de intermediação financeira, aprovando operações, participando dos comitês de decisão, definindo políticas e diretrizes e estabelecendo estratégias operacionais. Desenvolvem produtose serviços;dirigemequipes;controlam o desenvolvimento das operações financeiras, assegurando que sejam realizadas dentro das normas ditadas pelos órgãos reguladores, como também pelas políticas de controle interno da instituição; comunicam-se oralmente e por escrito.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1231", "Diretores administrativos e financeiros", "Dirigem o fluxo financeiro da empresa; implementam o orçamento empresarial e administram recursos humanos. Controlam patrimônio, suprimentos e logística e supervisionam serviços complementares. Coordenam serviços de contabilidade e controladoria e elaboram planejamento da empresa.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1232", "Diretores de recursos humanos e relações de trabalho", "Lideram atividades do departamento pessoal; formulam políticas de cargos, salários e benefícios; desenvolvem estratégias de seleção, treinamento e desenvolvimento de pessoal. Participam na formulação e execuçãode estratégias e planos de negócios da empresa; coordenam políticas de integração e eventos, na empresa e comunidade externa; conduzem atividades de integração de recursos humanos, em processos de fusão e integração.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1233", "Diretores de comercialização e marketing", "Atuam na definição do planejamento estratégico da empresa; definem e executam plano de marketing e vendas; gerem a qualidade da venda. Participam da definição de políticas de recursos humanos. Comunicam-se primordialmente para disseminar informações ao público de interesse da empresa.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("1234", "Diretores de suprimentos e afins", "Planejam, no mais alto nível da empresa,os serviços de suprimentos; dirigem atividades de compras; definem política de logística de suprimentos; participam das definições estratégicas para investimento e venda de ativo imobilizado; administram recursos humanos e materiais; comunicam-se, oralmente e por escrito, reportando andamento de projetos, riscos, custos e outras informações para tomada de decisões.", "N", "202308", "01/2017A", "2");
