INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2033", "Pesquisadores das ciências da saúde", "Os profissionais desta família desenvolvem pesquisas em ciências da saúde, nas áreas de clínica médica,medicina básica,medicina veterinária e em saúde coletiva e, para tanto, elaboram e planejam projetos de pesquisa, formam recursos humanos em pesquisa e divulgam resultados e informações. Podem prestarassistência comunitária , orientar políticas públicas e prestar assessorias na área das ciências da saúde. Podem também ministrar aulas.", "S", "202308", "01/2017A", "2");
