INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2239", "Terapeutas ocupacionais, ortoptistas e psicomotricistas", "Atendem pacientes e clientes para prevençao, habilitaçao e reabilitaçao de pessoas utilizando protocolos e procedimentos específicos de terapia ocupacional e ortoptia . Habilitam pacientes e clientes; realizam diagnósticos específicos; analisam condiçoes dos pacientes e clientes. Orientam pacientes, clientes, familiares, cuidadores e responsáveis; avaliam baixa visao; ministram testes e tratamentos ortópticos no paciente. Desenvolvem programas de prevençao, promoçao de saúde e qualidade de vida;", "S", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2241", "Profissionais da educação física", "Desenvolvem, com crianças, jovens e adultos, atividades físicas; ensinam técnicas desportivas;realizam treinamentos especializados com atletas de diferentes esportes; instruem-lhes acerca dos princípios e regras inerentes a cada um deles; avaliam e supervisionam o preparo físico dos atletas; acompanham e supervisionam as práticas desportivas; elaboram informes técnicos e científicos na área de atividades físicas e do desporto.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2251", "Médicos Clínicos", "Realizam consultas e atendimentos médicos; tratam pacientes e clientes; implementam ações de prevenção de doenças e promoção da saúde tanto individuais quanto coletivas; coordenam programas e serviços em saúde, efetuam perícias, auditorias e sindicâncias médicas; elaboram documentos e difundem conhecimentos da área médica.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2252", "Médicos em Especialidades Cirúrgicas", "Realizam intervenções cirúrgicas de acordo com a necessidade de cada paciente, implantam órteses e próteses, transplantam orgãos e tecidos; realizam consultas e atendimentos médicos para tratamento de pacientes; implementam ações de prevenção de doenças e promoção da saúde tanto individuais quanto coletivas; elaboram documentos médicos, administram serviços em saúde e difundem conhecimentos da área médica.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2253", "Médicos em Medicina Diagnóstica e Terapêutica", "Realizam procedimentos de diagnóstico e terapêutica; realizam consulta e atendimento médico; tratam pacientes como ser biopsicosocial; implementam ações para promoção da saúde; coordenam serviços de saúde; elaboram documentos médicos e difundem conhecimentos médicos.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2261", "Osteopatas e quiropraxistas", "Tratam e atendem pacientes e clientes aplicando tratamento manipulativo osteopático (TMO) e realizando ajustes quiropráticos. Estabelecem diagnóstico osteopático e quiroprático. Atuam na orientação de pacientes, clientes, familiares e cuidadores. Desenvolvem programas de prevenção, promoção de saúde e qualidade de vida. Exercem atividades técnico-científicas através da realização de pesquisas, trabalhos específicos, organização e participação em eventos científicos.", "S", "202308", "01/2017A", "2");
