INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2311", "Professores de nível superior na educação infantil", "Promovem educação e a relação ensino-aprendizagem de crianças de até seis anos; cuidam de alunos; planejam a prática educacional e avaliam as práticas pedagógicas. Organizam atividades; pesquisam; interagem com a família e a comunidade e realizam tarefas administrativas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2312", "Professores de nível superior do ensino fundamental (primeira a quarta séries)", "Ministram aulas (comunicação e expressão, integração social e iniciação às ciências) nas quatro primeiras séries do ensino fundamental. Preparam aulas; efetuam registros burocráticos e pedagógicos; participam na elaboração do projeto pedagógico; planejam o curso de acordo com as diretrizes educacionais. Atuam em reuniões administrativas e pedagógicas; organizam eventos e atividades sociais, culturais e pedagógicas. Para o desenvolvimento das atividades utilizam constantemente capacidades de comu", "N", "202308", "01/2017A", "2");
