INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2321", "Professores do ensino médio", "Ministram aulas teóricas e práticas no ensino médio, em escolas da rede pública e privada; acompanham a produção da área educacional e cultural; planejam o curso, a disciplina e o projeto pedagógico; avaliam o processo de ensino-aprendizagem; preparam aulas e participam de atividades institucionais. Para o desenvolvimento das atividades é mobilizado um conjunto de capacidades comunicativas.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2331", "Professores do ensino profissional", "Ensinam a jovens e adultos conhecimentos teóricos e práticos de uma área profissional; planejam o trabalho docente; avaliam a aprendizagem e o ensino;realizam pesquisas das mudanças no seu campo de ensino para transformar esse conhecimento em aulas e situações laboratoriais. Desenvolvem recursos didáticos, produzem registros escritos e gráficos; trabalham com higiene e segurança e promovem educação ambiental. Podem realizar trabalhos técnicos e de assessoria. No exercício das atividades mobiliza", "S", "202308", "01/2017A", "2");
