INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2341", "Professores de matemática, estatística e informática do ensino superior", "Lecionam matemática, estatística e computação, realizam pesquisas, produzem trabalhos acadêmicos em sua área de competência; orientam alunos, planejam e implementam cursos e disciplinas, avaliam desempenho do aluno, de programas e instituições. Coordenam atividades acadêmicas e científicas. Podem prestar assessorianas áreas técnica e científica e colaborar em atividades institucionais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2342", "Professores de ciências físicas, químicas e afins do ensino superior", "Exercem atividades didáticas, preparam material didático, formam profissionais e coordenam cursos de física, de química e de astronomia de graduação e/ou pós-graduação. Desenvolvem e realizam atividades de pesquisa e extensão. Atualizam seus conhecimentos, atuam em políticas públicas e participam do processo de avaliação institucional. Atuam na administração de instituições de ensino superior, definem infra-estrutura pertinente e estruturam processos seletivos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2343", "Professores de arquitetura e urbanismo, engenharia, geofísica e geologia do ensino superior", "Desenvolvem no ensino superior atividades didáticas, de pesquisa e de extensão nas áreas de conhecimento da engenharia, arquitetura e urbanismo, geofísica e geologia e em áreas afins, podendo exercer atividades administrativas institucionais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2344", "Professores de ciências biológicas e da saúde do ensino superior", "Ministram atividades didáticas; preparam aulas teóricas e práticas para disciplinas das ciências biológicas e da saúde, do ensino superior; planejam cursos; realizam atividades de extensão e divulgam produção acadêmica. Orientam e avaliam alunos; participam da administração universitária; avaliam disciplinas e cursos; comunicam-se oralmente e por escrito. Podem desenvolver pesquisas.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2345", "Professores na área de formação pedagógica do ensino superior", "Ensinam, articulando o processo de ensino-aprendizagem na formação de profissionais da educação; planejam atividades relativas a cursos e pesquisas; realizam pesquisas científicas sobre o campo educacional; supervisionam formação pedagógica em estágios; orientam alunos; avaliam o trabalho acadêmico científico; coordenam atividades de ensino, pesquisa e extensão. Produzem material de trabalho; prestam atendimento às demandas da comunidade na área da educação escolar e não-escolar (educação formal", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2346", "Professores nas áreas de língua e literatura do ensino superior", "Ministram aulas, cursos e seminários e realizam pesquisas, nas áreas de linguística e literatura; orientam alunos; realizam atividades pedagógico-administrativas, de avaliação e de qualificação profissional; organizam a produção do conhecimento na área; divulgam conhecimentos científicos; prestam assessoria e consultoria.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2347", "Professores de ciências humanas do ensino superior", "Preparam e ministram aula nas áreas de ciências humanas no ensino superior e orientam trabalhos acadêmicos; elaboram planos de ensino; supervisionam estágio; avaliam processos de ensino-aprendizagem; participam de processos de seleção e avaliação. Prestam assessoria técnico-científica; exercem atividades acadêmico-administrativas e constroem projetos político-pedagógicos. Podem desenvolver atividades de pesquisa e extensão.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2348", "Professores de ciências econômicas, administrativas e contábeis do ensino superior", "Ministram aulas, preparam cursos, programas, disciplinas e aulas, na área de ciências econômicas, administrativas e contábeis, do ensino superior; assistem e avaliam alunos;produzem trabalhos acadêmicos e orientam a elaboração de teses, monografias, dissertações e trabalhos de uso. Participam de atividades administrativas, bancas examinadoras e eventos acadêmicos; administram departamentos de ensino; realizam atividades de extensão e comunicam-se oralmente epor escrito.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2349", "Professores de artes do ensino superior", "Atuam no ensino superior, nas áreas de artes; planejam cursos; desenvolvem pesquisas e criações artísticas; coordenam atividades de extensão e divulgam conhecimentos específicos em artes. Exercem e assessoram atividades artísticas, pedagógicas e acadêmico-administrativas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2392", "Professores de educação especial", "Promovem a educação de alunos com necessidades educativas especiais ensinando-os a ler e escrever em português e em braile, calcular, expressar-se, resolver problemas e as atividades da vida diária, desenvolver habilidades, atitudes e valores; desenvolvem atividades funcionais e programas de estimulação essencial e de educação de jovens e adultos, avaliando as necessidades educacionais dos alunos; realizam atividades como: planejar, avaliar, elaborar materiais, pesquisar e divulgar conhecimentos", "S", "202308", "01/2017A", "2");
