INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2512", "Economistas", "Analisam o ambiente econômico; elaboram e executam projetos de pesquisa econômica,de mercado e de viabilidade econômica, dentre outros. Participam do planejamento estratégico e de curto prazo e avaliam políticas de impacto coletivo para o governo, ong e outras organizações. Gerem programação econômico-financeira; atuam nos mercados internos e externos; examinam finanças empresariais. Podem exercer mediação, perícia e arbitragem.", "N", "202308", "01/2017A", "2");
