INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2522", "Contadores e afins", "Legalizam empresas, elaborando contrato social/estatuto e notificando encerramento junto aos órgãos competentes; administram os tributos da empresa; registram atos e fatos contábeis; controlam o ativo permanente; gerenciam custos; administram o departamento pessoal; preparam obrigações acessórias, tais como: declarações acessórias ao fisco, órgãos competentes e contribuintes e administra o registro dos livros nos órgãos apropriados; elaboram demonstrações contábeis; prestam consultoria e informa", "N", "202308", "01/2017A", "2");
