INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2525", "Profissionais de administração ecônomico-financeira", "Administram fundos e carteiras de investimentos em instituições financeiras. Desenvolvem, implantam e administram produtos e serviços bancários. Analisam operações de crédito e de cobrança e operacionalizam contratos de financiamento e/ou empréstimos. Controlam recursos para crédito obrigatório e gerenciam cobranças. Preparam e consolidam informações gerenciais e econômico-financeiras. Relatam aos setores e clientes do banco, oralmente ou por escrito, a situação dos produtos e serviços bancários", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2526", "Profissionais da Administração dos Serviços de Segurança", "Gerenciam as atividades de segurança em geral. Elaboram planos e políticas de segurança. Realizam análises de riscos, adotam medidas preventivas e corretivas para proteger vidas, o patrimônio e restaurar as atividades normais de empresas. Administram equipes, coordenam serviços de inteligência empresarial e prestam consultoria e assessoria.", "S", "202308", "01/2017A", "2");
