INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2543", "Auditores fiscais do trabalho", "Asseguram a observância dos direitos trabalhistas nas relações de trabalho, desenvolvendo atividades de auditoria, levantando riscos ocupacionais, atendendo ao público, mediando conflitos individuais e coletivos, promovendo direitos de cidadania no trabalho e aplicando, se necessário, medidas punitivas aos infratores. Podem subsidiar a elaboração deplanos, programas e normas na área trabalhista e administrar atividades de fiscalização.", "N", "202308", "01/2017A", "2");
