INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2624", "Artistas visuais, desenhistas industriais e conservadores-restauradores de bens culturais", "Criam e executam obras de arte ou a aplicam as artes visuais associadas ao conhecimento tecnológico para conceber a forma e a funcionalidade de produtos e serviços; pesquisam temas, elaboram propostas, realizam pesquisas e divulgam os produtos e as obras concebidas.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2625", "Atores", "Interpretam e representam um personagem, uma situação ou idéia, diante de um público ou diante das câmeras e microfones, a partir de improvisação ou de um suporte de criação ( texto, cenário, tema etc) e com o auxílio de técnicas de expressão gestual e vocal.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2626", "Músicos compositores, arranjadores, regentes e musicólogos", "Compoem e arranjam obras musicais, regem e dirigem grupos vocais, instrumentais ou eventos musicais. Estudam, pesquisam e ensinam música. Editoram partituras, elaboram textos e prestam consultoria na área musical.", "N", "202308", "01/2017A", "2");
