INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2628", "Artistas da dança (exceto dança tradicional e popular)", "Concebem e concretizam projeto cênico em dança, realizando montagens de obras coreográficas; executam apresentações públicas de dança e, para tanto, preparam o corpo, pesquisam movimentos, gestos, dança,e ensaiam coreografias. Podem ensinar dança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2629", "Designer de interiores de nível superior", "Projetam e executam de forma criativa e científica soluções para espaços interiores residenciais, comerciais e institucionais, visando a estética, a eficiência, a segurança, a saúde e o conforto. Pesquisam produtos, materiais e equipamentos para elaboração e execução de projetos de interiores.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2631", "Ministros de culto, missionários, teólogos e profissionais assemelhados", "Realizam liturgias, celebrações, cultos e ritos; dirigem e administram comunidades; formam pessoas segundo preceitos religiosos das diferentes tradições; orientam pessoas; realizam ação social junto à comunidade; pesquisam a doutrina religiosa; transmitem ensinamentos religiosos; praticam vida contemplativa e meditativa; preservam a tradição e, para isso, é essencial o exercício contínuo de competências pessoais específicas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("2711", "Chefes de Cozinha e Afins", "Criam e elaboram pratos e cardápios, atuando direta e indiretamente na preparação dos alimentos. Gerenciam brigada de cozinha e planejam as rotinas de trabalho. Podem gerenciar, ainda, os estoques e atuar na capacitação de funcionários", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3001", "Técnicos em mecatrônica", "Auxiliam os engenheiros em projetos, programas, controle, instalação e manutenção de sistemas de automação. Analisam especificações para aquisição de componentes e equipamentos. Atuam em equipe, podendo coordená-la.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3003", "Técnicos em eletromecânica", "Planejam, executam e participam da elaboração de projetos eletromecânicos de máquinas, equipamentos e instalações. Usinam peças e interpretam esquemas de montagem e desenhos técnicos. Montam máquinas; fazem entrega técnica e realizam manutenção eletromecânica de máquinas, equipamentos e instalações. Podem coordenar e liderar equipes de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3011", "Técnicos de laboratório industrial", "Executam ensaios físicos, químicos, metalográficos e biológicos. Garantem a calibração dos equipamentos e realizam amostragem de materiais. Trabalham segundo normas de segurança, saúde e meio ambiente. Controlam a qualidade. Participam do sistema da qualidade da empresa e no desenvolvimento de novos produtos e fornecedores. Colaboram no desenvolvimento de metodologias de análises.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3012", "Técnicos de apoio à bioengenharia", "Desenvolvem técnicas aplicadas a bioengenharia; testam biomateriais; providenciam material e auxiliam pesquisas aplicadas à bioengenharia; realizam manutenção, em terceiro nível, de equipamentos de bioengenharia e médico-hospitalares; elaboram manuais de orientação e treinam profissionais iniciantes e usuários.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3111", "Técnicos químicos", "Executam ensaios físico-químicos, participam do desenvolvimento de produtos e processos, da definição ou reestruturação das instalações industriais; supervisionam operação de processos químicos e operações unitárias de laboratório e de produção, operam máquinas e/ou equipamentos e instalações produtivas, em conformidade com normas de qualidade, de boas práticas de manufatura, de biossegurança e controle do meio-ambiente. Interpretam manuais, elaboram documentação técnica rotineira e de registros", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3112", "Técnicos de produção de indústrias químicas, petroquímicas, refino de petróleo, gás e afins", "Programam atividades e coordenam processos de produção petroquímica. Controlam a qualidade de insumos e produtos. Analisam dados estatísticos do processo produtivo; interpretam laudos de análises químicas e identificam produtos e insumos. Mantêm equipamentos e materiais em condições operacionais e coordenam equipe de trabalho. Trabalham conforme normas e procedimentos técnicos de qualidade, de segurança, de preservação ambiental e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3113", "Técnicos em materiais, produtos cerâmicos e vidros", "Planejam, coordenam, orientam e supervisionam processos das etapas de produção de produtos de cerâmica e vidros.definem matérias-primas para produção; planejam e realizam ensaios físico-químicos; desenvolvem produtos e programam produção. Controlam e corrigem desvios nos processos manuais e automatizados; operam máquinas e equipamentos; desenvolvem melhorias no processo produtivo. Prestam assistência técnica; registram informações técnicas e administrativas e capacitam pessoas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3114", "Técnicos em fabricação de produtos plásticos e de borracha", "Projetam, planejam, supervisionam, controlam e executam processos de fabricação de produtos de plástico e de borracha. Acompanham sistemas de produção, projetam ferramentas e dispositivos. Realizam ensaios físico-químicos em laboratórios. Atendem clientes; orientam, apóiam e acompanham tecnicamente os fornecedores. Definem matérias-primas, utilizam instrumentos de medição e recursos de informática. Interpretam normas e procedimentos integrados ao sistema de qualidade e gestão ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3115", "Técnicos em controle ambiental, utilidades e tratamento de efluentes", "Auxiliam profissionais de nível superior na implementação de projetos, gestão ambiental e coordenação de equipes de trabalho; operam máquinas, equipamentos e instrumentos. Coordenam processos de controle ambiental, utilidades, tratamento de efluentes e levantamentos meteorológicos. Realizam análises físico-químicas e microbiológicas dos efluentes. Monitoram a segurança no trabalho", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3116", "Técnicos têxteis", "Administram e controlam atividades técnicas dos processos da cadeia de produção têxtil tais como beneficiamento, fiação, tecelagem e malharia,assegurando conformidade a padrões de qualidade e gerenciando orçamento da produção. Participam de desenvolvimento de produtos e métodos de trabalho, bem como ministram cursos à equipe de trabalho sob sua liderança.", "N", "202308", "01/2017A", "2");
