INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3121", "Técnicos em construção civil (edificações)", "Realizam levantamentos topográficos e planialtimétricos. Desenvolvem e legalizam projetos de edificações sob supervisão de um engenheiro civil; planejam a execução,orçam e providenciam suprimentos e supervisionam a execução de obras e serviços. Treinam mão-de-obra e realizam o controle tecnológico de materiais e do solo.", "N", "202308", "01/2017A", "2");
