INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3123", "Técnicos em geomática", "Executam levantamentos geodésicos e topohidrográficos, por meio de levantamentos altimétricos e planimétricos; implantam, no campo, pontos de projeto, locando obras de sistemas de transporte, obras civis, industriais, rurais e delimitando glebas; planejam trabalhos em geomática; analisam documentos e informações cartográficas, interpretando fotos terrestres, fotos aéreas, imagens orbitais, cartas, mapas, plantas, identificando acidentes geométricos e pontos de apoio para georeferenciamento e ama", "N", "202308", "01/2017A", "2");
