INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3132", "Técnicos em eletrônica", "Consertam e instalam aparelhos eletrônicos, desenvolvem dispositivos de circuitos eletrônicos, fazem manutenções corretivas, preventivas e preditivas, sugerem mudanças no processo de produção, criam e implementam dispositivos de automação. Treinam, orientam e avaliam o desempenho de operadores. Estabelecem comunicação oral e escrita para agilizar o trabalho, redigem documentação técnica e organizam o local de trabalho. Podem ser supervisionados por engenheiros eletrônicos. Consertam e instalam a", "N", "202308", "01/2017A", "2");
