INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3142", "Técnicos mecânicos (ferramentas)", "Pesquisam o mercado, desenvolvem e realizam manutenção de ferramentas e dispositivos para fabricação mecânica. Providenciam recursos técnicos para a fabricação do produto final para o qual foram desenvolvidas as ferramentas. Planejam a manutenção preventiva e corretiva de equipamentos e ferramentas, bem como propoem melhoria contínua de processos de fabricação, segundo critérios de qualidade e segurança no trabalho. Podem prestar assistência técnica.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3143", "Técnicos em mecânica veicular", "Realizam ensaios e testes e montam componentes na fabricação e manutenção veicular automobilística, naval e aeronáutica, de acordo com normas de qualidade e de segurança do trabalho. Prestam assessoria a equipes internas e externas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3144", "Técnicos mecânicos na manutenção de máquinas, sistemas e instrumentos", "Planejam a manutenção de máquinas, sistemas e instrumentos; supervisionam processos de manutenção; mantêm equipamentos, instrumentos, máquinas e sistemas em condições plenas de funcionamento e calibram instrumentos e equipamentos. Elaboram procedimentos técnicos e administrativos; propoem melhorias em máquinas, instrumentos e sistemas; aplicam técnicas de segurança e normas ambientais; prestam assessoria técnica em manutenção e realizam testes e ensaios.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3146", "Técnicos em metalurgia (estruturas metálicas)", "Planejam e supervisionam a execução das atividades de caldeiraria, soldagem e estruturas metálicas, de acordo com a programação de produção. Qualificam procedimentos de soldagem e inspecionam processos de fabricação de acordo com normas de qualidade, preservação do meio ambiente e segurança do trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3147", "Técnicos em siderurgia", "Elaboram padrões técnicos e operacionais de produção siderúrgica, promovem meios para o desenvolvimento profissional de equipes de trabalho; desenvolvem inovações em produtos e tecnologias siderúrgicas, programam e monitoram processos de fabricação de produtos siderúrgicos, elaboram relatórios técnicos de siderurgia; atuam no controle de qualidade e prestam assistência técnica de produtos; calculam variáveis de controle da produção siderúrgica.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3148", "Especialistas em inspeções industriais", "Atuam nas fases de construção, montagem e operação de plantas industriais e outras inspecionando equipamentos e instalações industriais, tais como; caldeiras, vasos de pressão, tubulações, dutos etc. Inspecionam processos industriais, realizam testes, ensaios e controles dimensionais, de acordo com normas técnicas e regulamentadoras. Emitem pareceres sobre as condições dos equipamentos e instalações inspecionados e elaboram relatórios técnicos.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3161", "Técnicos em geologia", "Auxiliam geólogos e engenheiros nos trabalhos de prospecção de recursos minerais. Coletam amostras e processam dados geológicos, geofísicos e geoquímicos. Preparam amostras minerais e monitoram processos de análises laboratoriais. Identificam áreas de potencial mineral por meio de levantamentos topográficos, geológicos e cartográficos. Controlam a qualidade de frente de lavra e participam de estudos de impacto ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3163", "Técnicos em mineração", "Lavram jazidas minerais; supervisionam processos de beneficiamento de minério. Participam da prospecção e pesquisa de minerais. Coletam amostras de minerais; processam dados de prospecção, pesquisa e lavra; participam do planejamento de atividades de mineração. Controlam a movimentação da produção final de minério e analisam a qualidade e quantidade do produto mineral. Fiscalizam equipes de trabalho para cumprimento de normas de saúde e segurança e participam de projetos ambientais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3171", "Técnicos de desenvolvimento de sistemas e aplicações", "Desenvolvem sistemas e aplicações, determinando interface gráfica, critérios ergonômicos de navegação, montagem da estrutura de banco de dados e codificação de programas; projetam, implantam e realizam manutenção de sistemas e aplicações; selecionam recursos de trabalho, tais como metodologias de desenvolvimento de sistemas, linguagem de programação e ferramentas de desenvolvimento. Planejam etapas e ações de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3172", "Técnicos de suporte e monitoração ao usuário de tecnologia da informação.", "Operam sistemas de computadores e microcomputadores, monitorando odesempenho dos aplicativos, recursos de entrada e saída de dados, recursos de armazenamento de dados, registros de erros, consumo da unidade central de processamento (CPU), recursos de rede e disponibilidade dos aplicativos. Asseguram o funcionamento do hardware e do software; garantem a segurança das informações, por meio de cópias de segurança e armazenando-as em local prescrito, verificando acesso lógico de usuário e destruindo", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3180", "Desenhistas técnicos, em geral", "Analisam solicitações de desenhos; interpretam documentos de apoio, tais como plantas, projetos, catálogos, croquis e normas. Observam características técnicas de desenhos; esboçam desenhos; definem formatos e escalas, sistemas de representação e prioridades de desenhos, conforme cronogramas. Desenham detalhes de projetos de desenhos. Enviam desenhos para revisão; realizam cópias de segurança e disponibilizam desenhos finais e/ou revisões para áreas afins. São classificados nessa epígrafe os des", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3181", "Desenhistas técnicos da construção civil e arquitetura", "Elaboram desenhos de arquitetura e engenharia civil utilizando softwares específicos para desenho técnico, assim como podem executar plantas, desenhos e detalhamentos de instalações hidrossanitárias e elétricas e desenhos cartográficos; coletam e processam dados e planejam o trabalho para a elaboração do projeto como, por exemplo, interpretar projetos existentes, calcular e definir custos do desenho, analisar croqui e aplicar normas de saúde ocupacional e normas técnicas ligadas à construção civ", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3182", "Desenhistas técnicos da mecânica", "Executam desenhos, projeções e corte utilizando meios manuais e eletrônicos e preparam diagramas detalhados de máquinas e peças e de projetos navais e aeronáuticos, definindo os meios de execução do desenho e coletando dados do projeto, tais como incluir dimensões, métodos de ajuste e outras informações de engenharia, sob a supervisão de um desenhista projetista ou de um engenheiro; acompanham o processo de execução e montagem.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3183", "Desenhistas técnicos em eletricidade, eletrônica, eletromecânica, calefação, ventilação e refrigeraç", "Analisam solicitações para desenhos em eletroeletrônica; elaboram o desenho preliminar e desenho executivo de projetos de fabricação e instalação de máquinas e equipamentos de refrigeração, de instalação de sistemas de ventilação e calefação; desenham esquemas eletrônicos, leiautes de circuitos impressos, leiautes de quadros e componentes elétricos e projetos elétricos; submetem desenhos à aprovação; efetuam revisões e finalizam desenhos, preparando cópias, arquivando e registrando apontamentos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3184", "Desenhistas técnicos de produtos e serviços diversos", "Interpretam solicitações de desenhos; elaboram desenhos de produtos ou serviços; submetem desenhos à aprovação. Dão acabamento final em desenhos; indicam características de materiais e acabamentos em desenhos; organizam e solicitam adequação ergonômica do posto de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3185", "Desenhistas projetistas de construção civil e arquitetura", "Auxiliam arquitetos e engenheiros no desenvolvimento de projetos de construção civil e arquitetura; aplicam as normas de saúde ocupacional nr-9, nr-15 e nr-17; apóiam a coordenação de equipes; auxiliam a engenharia na coordenação de projetos; pesquisam novas tecnologias de produtos e processos; projetam obras de pequeno porte, coletando dados, elaborando ante projetos, desenvolvendo projetos, dimensionando estruturas e instalações, especificando materiais, detalhando projetos executivos e atuali", "N", "202308", "01/2017A", "2");
