INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3188", "Desenhistas projetistas e modelistas de produtos e serviços diversos", "Confeccionam moldes para roupas e calçados; pesquisam segmentos de mercado, estudando estilos de design e avaliando pesquisas sobre tendências de mercado; avaliam materiais para aquisição e desenvolvem protótipos de roupas, calçados e móveis; projetam móveis, interpretando desenhos e modelos, analisando o local de instalação de móveis sob medida, elaborando desenhos de móveis e gabaritos em cad e prancheta, dimensionando componentes, especificando madeiras, derivados de madeira e acessórios para", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3191", "Técnicos do vestuário", "Desenvolvem produtos de vestuário a partir de pesquisas de mercado, as quais definirão o público-alvo, as tendências da moda e as necessidades do mercado de vestuário. Desenvolvem fornecedores; planejam, executam e controlam programas de fabricação de indústrias do vestuário (roupas, calçados e artefatos);elaboram métodos e processos de produção. Podem assumir responsabilidade de uma ou várias funções (ex.: estudos, pesquisas, desenvolvimento, controle de qualidade e compras), dependendo do tama", "N", "202308", "01/2017A", "2");
