INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3211", "Técnicos agrícolas", "Prestam assistência e consultoria técnicas, orientando diretamente produtores sobre produção agropecuária, comercialização e procedimentos de biosseguridade. Executam projetos agropecuários em suas diversas etapas. Planejam atividades agropecuárias, verificando viabilidade econômica, condições edafoclimáticas e infra-estrutura. Promovem organização, extensão e capacitação rural. Fiscalizam produção agropecuária. Desenvolvem tecnologias adaptadas à produção agropecuária. Podem disseminar produção", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3212", "Técnicos florestais", "Supervisionam, execução de atividades florestais, desde a construção de viveiros florestais e infra-estrutura, produção de mudas e colheita florestal até o manejo de florestas nativas e comerciais; inventariam florestas, planejam atividades florestais; elaboram documentos técnicos. Administram unidades de conservação e de produção, atuam na preservação e conservação ambiental; fiscalizam e monitoram fauna e flora; ministram treinamentos e podem participar de pesquisas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3213", "Técnicos em aqüicultura", "Organizam a reprodução de animais aquáticos, como peixes, camarões, mexiliões,ostras eras, dentre outros;coletam material de reprodução; controlam sanidade e predação dos animais; monitoram qualidade da água, alimentam, capturam e beneficiam animais aquáticos de viveiros, tanques e fazendas marinhas. Prestam assistência técnica e auxiliam na elaboração de projetos, orientando construção de instalações em fazendas aqüícolas e sistema de criação de animais aquáticos. Podem ministrar cursos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3221", "Tecnólogos e técnicos em terapias complementares e estéticas", "Realizam prognósticos energéticos por meio de métodos da medicina tradicional chinesa para harmonização energética, fisiológica e psico-orgânica; aplicam estímulos físico-químicos e técnicas corporais para tratamento de moléstias psico-neuro-funcionais e energéticas (acupunturista). Prognosticam e tratam as patologias superficiais dos pés e deformidades podais utilizando-se de instrumental pérfuro-cortante, medicamentos de uso tópico, órteses e próteses (podólogo). Realizam ações prognósticas e ", "S", "202308", "07/2022A", "2");
