INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3223", "Técnicos em optica e optometria", "Realizam exames optométricos; confeccionam lentes; adaptam lentes de contato; montam óculos e aplicam próteses oculares. Promovem educação em saúde visual; vendem produtos e serviços ópticos e optométricos; gerenciam estabelecimentos. Responsabilizam-se tecnicamente por laboratórios ópticos, estabelecimentos ópticos básicos ou plenos e centros de adaptação de lentes de contato. Podem emitir laudos e pareceres ópticos-optométricos.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3224", "Técnicos de odontologia", "Planejam o trabalho técnico-odontológico, de nível médio,em consultórios, clínicas, laboratórios de prótese e em órgãos públicos de saúde. Previnemdoença bucal participando de projetos educativos e de orientação de higiene bucal. Confeccionam e reparam próteses dentárias humanas, animais e artísticas. Executam procedimentos odontológicos sob supervisão do cirurgião dentista. Administram pessoal e recursos financeiros e materiais. Mobilizam capacidades de comunicação em palestras, orientações e d", "S", "202308", "01/2017A", "2");
