INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3519", "Técnicos da inteligência", "Oferecem suporte no planejamento, execução, supervisão, coordenação e controle das atividades de Inteligência. Prestam apoio na capacitação de recursos humanos. Operam equipamentos, instrumentos e sistemas necessários à atividade de inteligência.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3522", "Agentes da saúde e do meio ambiente", "Orientam e fiscalizam as atividades e obras para prevenção/preservação ambiental e da saúde, por meio de vistorias, inspeções e análises técnicas de locais, atividades, obras, projetos e processos, visando o cumprimento da legislação ambiental e sanitária; promovem educação sanitária e ambiental.", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3523", "Agentes fiscais metrológicos e de qualidade", "Fiscalizam instrumentos de medição, medidas materializadas, produtos, marcas de conformidade e serviços, conforme legislação. Verificam instrumentos e medidas materializadas; realizam testes, análises e calibrações. Registram o processo de fiscalização, verificação e calibração; supervisionam atividades metrológicas; orientam o público; formam recursos humanos na área de metrologia.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3524", "Profissionais de direitos autorais e de avaliacão de produtos dos meios de comunicação", "Preservam e protegem os direitos do cidadão, avaliando os produtos dos meios de comunicação, sob os aspectos éticos, educativos e artísticos, podendo realizar sua classificação indicativa e qualitativa. Preservam os direitos do autor, analisando, arrecadando e distribuindo direitos autorais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3532", "Técnicos em operações e serviços bancários", "Processam operações de crédito, investimento e serviços bancários, obedecendo normas externas, emanadas de órgãos governamentais, e internas, da instituição que os empregam. Controlam as operações de concessão de crédito, investimento e serviços a pessoas físicas ou jurídicas, a fim de cumprir e fazer cumprir as normas e regras internas e de órgãos regulamentadores, tais como: banco central do brasil e secretaria da receita federal, entre outros. Atendem aos demais setores do banco, como seus cl", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3541", "Especialistas em promoção de produtos e vendas", "Planejam vendas especializadas; demonstram produtos e serviços; concretizam vendas. Acompanham clientes no pós-venda; contatam áreas internas da empresa. Sugerem políticas de vendas e participam de eventos.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3542", "Compradores", "Recebem requisições de compras, executam processo de cotação e concretizam a compra de serviços, produtos, matérias-primas e equipamentospara o comércio atacadista e varejista, para indústrias,empresas, órgãos públicos e privados. Acompanham o fluxo de entregas, desenvolvem fornecedores de materiais e serviços; supervisionam equipe e processos de compra. Preparam relatórios e fazem o papel de interlocutor entre requisitantes e fornecedores.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3543", "Analistas de comércio exterior", "Realizam operações de comércio internacional para importação e exportação de produtos e serviços; processam operações de importação; traçam planos de exportação; analisam mercado internacional de produtos e serviços; participam da promoção de produtos ou serviços em feiras e outros eventos, prestando orientação técnica aos visitantes ou participantes; orientam o desembaraço aduaneiro. Podem expressar-se em língua estrangeira.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3544", "Leiloeiros e avaliadores", "Captam, avaliam, oficializam, divulgam, administram e organizam leilões de bens móveis e imóveis, novos ou usados e semoventes. Emitem pareceres técnicos e comerciais sobre os bens a serem leiloados ou comercializados.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3545", "Corretores de seguros", "Vendem apólices de seguros de vida, de automóvel, de previdência privada, de incêndios, de riscos marítimos e outros ramos de seguros; relacionam-se com companhia seguradora e prestam assistência ao segurado; empregam técnicas de vendas e operacionalizam rotinas informatizadas. Podem administrar a corretora.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3546", "Corretores de imóveis e de grãos", "Intermedeiam compra, venda, permuta, locação e administração de imóveis e solicitam documentação. Para tanto, entrevistam clientes, pesquisam mercado e captam imóveis e elaboram estratégias de comercialização. Podem assessorar os clientes após transação.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3547", "Representantes comerciais autônomos", "Intermedeiam negócios mercantis para terceiros, utilizando mostruários, catálogos, panfletos e quaisquer outros meios ou instrumentos que possam facilitar as negociações junto à clientela. Planejam vendas,divulgam e demonstram produtos e serviços e finalizam vendas. Acompanham clientes pós-venda; interagem com as demais áreas da empresa representada e participam de eventos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3548", "Técnicos em serviços de turismo e organização de eventos", "Auxiliam no planejamento das atividades de turismo, montam pacotes de turismo, contratam, reservam, promovem, vendem e fiscalizamserviços turísticos, auxiliam a organização de eventos e prestam orientação técnica.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3711", "Técnicos em biblioteconomia", "Atuam no tratamento, recuperação e disseminação da informação e executam atividades especializadas e administrativas relacionadas à rotina de unidades ou centros de documentação ou informação, quer no atendimento ao usuário, quer na administração do acervo, ou na manutenção de bancos de dados. Participam da gestão administrativa, elaboração e realização de projetos de extensão cultural. Colaboram no controle e na conservação de equipamentos. Participam de treinamentos e programas de atualização.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3712", "Técnicos em museologia e afins", "Auxiliam especialistas das diversas áreas de museus, nos trabalhos de organização, conservação, pesquisa e difusão de documentos e objetos de caráter histórico, artístico, científico, literário ou de outra natureza.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3713", "Técnicos em artes gráficas", "Realizam programações visuais gráficas, prestam serviços de assistência técnica a clientes, buscam alternativas para melhoria em tecnologias e matérias - primas e implantam novas tecnologias. Operam máquinas e equipamentos de pré-impressão de produtos gráficos, planejam e controlam o processo de produção, realizam controle de qualidade das matérias - primas e do produto final. Podem coordenar equipe de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3714", "Recreadores", "Promovem atividades recreativas diversificadas, visando ao entretenimento, à integração social e ao desenvolvimento pessoal dos clientes. Para tanto, elaboram projetos e executam atividades recreativas;promovem atividades lúdicas, estimulantes à participação; atendem clientes, criam atividades recreativas e coordenam setores de recreação; administram equipamentos e materiais para recreação. As atividades são desenvolvidas segundo normas de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3721", "Captadores de imagens em movimento", "Captam imagens através de câmeras de cinema e vídeo para a realização de produções cinematográficas, televisivas e multimídia, com teor artístico, jornalístico, documental e publicitário. Captam imagens em movimento; interpretam visualmente o roteiro; executam conceito fotográfico e organizam produção de imagens, dialogando constantemente com a equipe de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3722", "Operadores de rede de teleprocessamento e afins", "Operam e monitoram sistemas de comunicação em rede, preparam equipamentos e meios de comunicação, cuidam da segurança operacional por meio de procedimentos específicos e realizam atendimento ao usuário.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3731", "Técnicos de operação de registros sonoro/audiovisuais", "Operam equipamentos de uma emissora de rádio; organizam e executam a programação da emissora; editam áudio (trilhas sonoras, músicas, vinhetas, comerciais, chamadas promocionais e programas) e mixam aúdio. No exercício das atividades mobilizam capacidades de administrar o tempo e de improvisar, além de capacidades comunicativas para interagir com as equipes técnica, de programação e comercial.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3732", "Supervisores operacionais e técnicos em mídias audiovisuais", "Coordenam atividades de operação de sistemas de televisão e produtoras de vídeo, nas fases de planejamento e execução, gerenciando recursos humanos, financeiros e uso dos equipamentos. Produzem eventos externos, manipulam áudio e vídeo; dirigem e capturam imagens, bem como realizam atividades de tratamento de áudio. Inserem caracteres, créditos e artes nos produtos gravados. Preparam estúdio de tv e produtoras de vídeo e executam roteiro de programação. Administram tráfego de sinal e monitoram t", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3741", "Técnicos em áudio", "Configuram, operam e monitoram sistemas de sonorização e gravação; editam, misturam, pré-masterizam e restauram registros sonoros de discos, fitas, vídeo, filmes etc. Criam projetos de sistemas de sonorização e gravação. Preparam, instalam e desinstalam equipamentos de áudio e acessórios.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3742", "Técnicos em cenografia", "Constroem cenários, adereços e mobiliários, a partir de análise de projeto cenográfico e pesquisa de objetos e materiais; executam técnicas afins, como trabalhos de carpintaria, serralheria, costura, pintura, modelagem e escultura; montam e adaptam peças de cenários e efeitos especiais ; operam maquinaria, como varas elétricas e cenográficas, cortinas, gruas, carrinhos sobre trilhos e mecanismos de efeitos especiais; supervisionam atividades relacionadas ao planejamento, orçamento e contratação ", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3743", "Técnicos em operação de aparelhos de projeção", "Operam aparelhos de projeção cinematográfica e equipamentos correspondentes de produção de imagem e som; verificam o funcionamento dos equipamentos de projeção. Montam e desmontam filmes e complementos; projetam filmes; controlam a qualidade da exibição de filmes; operam videocassete e videowall (telão).", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3744", "Técnicos em montagem, edição e finalização de mídia audiovisual", "Estruturam narrativas de filmes e vídeos; editam imagens e áudio; criam efeitos especiais. Assessoram pós-produção, determinando roteiro de dublagem, listando planos montados e indicando procedimentos para edição de som. Supervisionam finalização, dublagem e conformação de copião de filmes.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3751", "Designers de interiores, de vitrines e visual merchandiser e afins (nível médio)", "Projetam e executam soluções para espaços internos residenciais, comerciais e industriais visando a estética, o bem-estar e o conforto. Criam e projetam vitrines, ambientes comerciais e industriais que destaquem e valorizem o produto; projetam programações visuais com os objetivos de estimular o consumo de produtos e de informar o consumidor.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3761", "Dançarinos tradicionais e populares", "Os dançarinos tradicionais e populares dançam, sozinhos, em pares ou em grupo com fins ritualísticos, performáticos e espetaculares, pesquisam e estudam, reinterpretam danças tradicionais e populares; criam espetáculos, ministram aulas e inserem seu acervo cultural em diferentes contextos (sociais, pedagógicos e terapêuticos).", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3762", "Artistas de circo (circenses)", "Realizam, sozinhos ou em grupos, diversos tipos de representações, em um espetáculo público (circo, teatro, rua, estúdio de televisão). Criam números e os apresentam em cena, a partir de técnicas corporais (acrobacia, equilibrismo, malabarismo, ilusionismo, comédia, canto, dança, pantomima) ou de técnicas de adestramento de animais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3763", "Apresentadores de eventos, programas e espetáculos", "Apresentam e/ou animam programas de rádio e televisão, festas populares, eventos, atrações circenses ou outros tipos de espetáculos; orientam-se por roteiros ou fazem improvisaçõespara divertir, informar, instruir o público, telespectador ou ouvinte.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3764", "Modelos", "Posam para fotógrafos e artistas plásticos imobilizando o corpo segundo orientação artística ou criando poses próprias, em estúdios, escolas de arte e locações internas ou externas; mostram produtos em trabalhos publicitários (fotos, filmes e eventos) mobilizando habilidades expressivas que atraem o olhar, que sugerem comportamentos e estilos de vida e que representam o tipo de pessoa que se quer associar ao produto; desfilam em passarelas ou em espaços determinados, onde sincronizam movimentos ", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3771", "Atletas profissionais", "Tomam parte como profissionais em competições e provas esportivas. Participam, individualmente ou coletivamente, de competições esportivas, em caráter profissional.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3772", "Arbitros desportivos", "Zelam pela observância do regulamento nas competições esportivas, controlando o andamento das mesmas, registrando as infrações, aplicando as penalidades e fazendo as marcações necessárias para assegurar o processamento desses eventos dentro das normas estabelecidas pelos órgãos desportivos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3911", "Planejadores, programadores e controladores de produção e manutenção", "Planejam, controlam e programam a produção; controlam suprimentos (matéria-prima e outros insumos). Planejam a manutenção de máquinas e equipamentos. Tratam informações em registros de cadastros e relatórios e na redação de instruções de trabalho.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3912", "Técnicos de controle da produção", "Inspecionam o recebimento e organizam o armazenamento e movimentação de insumos; verificam conformidade de processos; liberam produtos e serviços; trabalham de acordo com normas e procedimentos técnicos, de qualidade e de segurança e demonstram domínio de conhecimentos técnicos específicos da área.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("3951", "Técnicos de apoio em pesquisa e desenvolvimento", "Planejam,preparam e executam ensaios para as mais diversas áreas de pesquisa e desenvolvimento, supervisionados por profissional de nível superior. Analisam resultados de ensaios; auxiliam no desenvolvimento de métodos, processos e produtos. Podem exercer atividades auxiliares de difusão de pesquisa e desenvolvimento.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4101", "Supervisores administrativos", "Supervisionam rotinas administrativas em instituições públicas e privadas, chefiando diretamente equipe de escriturários, auxiliares administrativos, secretários de expediente, operadores de máquina de escritório e contínuos. Coordenam serviços gerais de malotes, mensageiros, transporte, cartório, limpeza, terceirizados, manutenção de equipamento, mobiliário, instalações etc; administram recursos humanos, bens patrimoniais e materiais de consumo; organizam documentos e correspondências; gerencia", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4102", "Supervisores de serviços financeiros, de câmbio e de controle", "Supervisionam e controlam equipe e serviços financeiros, de câmbio, bens-patrimoniais, créditos e bancários; elaboram orçamentos; efetuam e conferem pagamentos; realizam cobranças, planejando e solucionando pendências; administram almoxarifado.", "N", "202308", "01/2017A", "2");
