INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4121", "Operadores de equipamentos de entrada e transmissão de dados", "Organizam a rotina de serviços e realizam entrada e transmissão de dados, operando teleimpressoras e microcomputadores; registram e transcrevem informações, operando máquinas de escrever; atendem necessidades do cliente interno e externo. Supervisionam trabalho e equipe e negociam serviço com cliente.", "N", "202308", "01/2017A", "2");
