INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4132", "Escriturários de serviços bancários", "Prestam atendimento a usuários de serviços bancários; realizam operações de caixa; fornecem documentos aos clientes e executam atividades de cobrança. Apoiam as atividades da agências e demais setores do banco; administram fluxo de malotes; compensam documentos e controlam documentação de arquivos. Estabelecem comunicação com os clientes, prestando-lhes informações sobre os serviços bancários.", "N", "202308", "01/2017A", "2");
