INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4142", "Apontadores e conferentes", "Apontam a produção e controlam a freqüência de mão-de-obra. Acompanham atividades de produção, conferem cargas e verificam documentação. Preenchem relatórios, guias, boletins, plano de carga e recibos. Controlam movimentação de carga e descarga nos portos, terminais portuários e embarcações. Podem liderar equipes de trabalho.", "N", "202308", "01/2017A", "2");
