INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4152", "Trabalhadores nos serviços de classificação e entregas de correspondências, encomendas e publicações", "Recebem e expedem cargas, malas e malotes e objetos de serviços de correio. Coletam, ordenam, conferem, fazem triagem e entregam cargas eobjetos tais como: encomendas, cartas, caixas, malotes e contêineres. Prestam contas dos objetos coletados e entregues. Pesquisam e rastreiam objetos. Prestam informações e participam de disseminação de campanhas públicas. Também fazem parte desta família ocupacional os trabalhadores similares que atuam em empresas de encomendas expressas (courier).", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4153", "Trabalhadores em registros e informações em saúde", "Coletam e registram dados clínicos dos pacientes e/ou dados do tumor. Analisam dados clínicos e assistenciais dos pacientes. Realizam codificação clínica, consultando classificações internacionais de doenças e atribuindo códigos padrão para doenças e agravos de saúde e procedimentos. Levantam dados junto às fontes notificadoras e realizam o seguimento do tumor.", "S", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4201", "Supervisores de atendimento ao público e de pesquisa", "Supervisionam diretamente, lideram e motivam uma equipe de trabalhadores que prestam serviços de atendimento ao público ou coletam informações e documentos, tais como, caixas e bilheteiros, coletadores de apostas e de jogos, recepcionistas, telefonistas e operadores de telemarketing e atendimento, cobradores, entrevistadores e recenseadores, intermediando relações entre clientes e prestadores de serviços e ou funcionários e entre pesquisadores e informantes; planejam e administram o trabalho da ", "N", "202308", "01/2017A", "2");
