INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("4242", "Aplicadores de provas e afins", "Aplicam provas (concurso, avaliação e exame), seguindo as normas de segurança e sigilo do evento; Planejam as ações e preparam o evento; Selecionam espaços físicos de acordo com o perfil do evento; Gerenciam equipe de colaboradores, capacitando-os e alocando-os de acordo com a função e perfil.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5101", "Supervisores dos serviços de transporte, turismo, hotelaria e administração de edifícios", "Planejam rotinas de trabalho em restaurantes, hotéis, transportes e administração de edifícios; treinam funcionários em hospedagem, transportes e alimentação e coordenam equipes de trabalho. Atendem clientes em hotéis, bilheterias e restaurantes; avaliam o desempenho de funcionários, a execução de serviços e relatórios de operação e de avaliação. Verificam manutenção de instalações, equipamentos e utensílios e preparam alimentos e bebidas.", "N", "202308", "01/2017A", "2");
