INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5111", "Trabalhadores de segurança e atendimento aos usuários nos transportes", "Checam equipamentos e instalações das aeronaves, trens e embarcações; prestam serviços aos usuários de transportes aéreos, ferroviários e fluviais;demonstram aos passageiros os procedimentos de segurança e emergência; servem refeições preparadas e bebidas; orientam usuários sobre procedimentos de segurança e promovem o entretenimento e o bem-estar dos usuários. Controlam a entrada e a saída de alimentos e materiais de limpeza; zelam pela manutenção da limpeza. Agem em situações de emergência. Cu", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5112", "Fiscais e cobradores dos transportes coletivos", "Organizam e fiscalizam as operações dos ônibus e outros veículos de transporte coletivo como, condições de operação dos veículos, cumprimento dos horários, entre outros. Preenchem relatórios; preparam escalas de operadores; examinam veículos e atendem usuários. Agem na solução de ocorrências. Executam a venda de bilhetes em veículos, estações metropolitanas, ferroviárias e similares e administram valores.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5114", "Guias de turismo", "Executam roteiro turístico, transmitem informações, atendem passageiros, organizam as atividades do dia, realizam tarefas burocráticas e desenvolvem itinerários e roteiros de visitas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5115", "Condutores de turismo", "Conduzem clientes/pessoas nas atividades de turismo pesca e de aventura, tais como: rafting, escalada, trilha, balonismo, etc, operando veículos e equipamentos diversos, descrevendo características físicas, ambientais e históricas do local onde atuam. Organizam, selecionam e preparam materiais e equipamentos necessários à realização das atividades turísticas. Orientam os clientes/pessoas nos procedimentos das atividades turísticas que irão realizar, nas questões de segurança e cuidados com meio", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5121", "Trabalhadores dos serviços domésticos em geral", "Preparam refeições e prestam assistência às pessoas, cuidam de peças do vestuário como roupas e sapatos e colaboram na administração da casa, conforme orientações recebidas. Fazem arrumação ou faxina e podem cuidar de plantas do ambiente interno e de animais domésticos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5131", "Mordomos e governantas", "Atendem hóspedes e familiares, organizando e supervisionando os trabalhos dos funcionários das áreas de governança e mordomia de hotéis, residências, hospitais ou estabelecimentos similares, recepcionando convidados e visitantes, acompanhando entradas e saídas dos hóspedes durante a estadia, familiares em compromissos externos e viagens da família. Cuidam do vestuário e objetos de hóspedes e familiares. Supervisionama arrumação de aposentos e demais áreas. Servem alimentos e bebidas e supervisio", "N", "202308", "01/2017A", "2");
