INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5133", "Camareiros, roupeiros e afins", "Limpam, arrumam, organizam, vistoriam e abastecem apartamentos, camarotes,camarins, quartos e banheiros, em hotéis, teatros, estúdios de televisão e cinema, navios e estabelecimentos similares;mantém em ordem, conservam, classificam e organizam os figurinos e vestuários de clientes e atores, identificando e controlando as saídas, devoluções e substituições das roupas e acessórios; atendem e auxiliam os hóspedes e atores em suas solicitações e necessidades pessoais e profissionais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5134", "Trabalhadores no atendimento em estabelecimentos de serviços de alimentação, bebidas e hotelaria", "Atendem os clientes, recepcionando-os e servindo refeições e bebidas em restaurantes, bares, clubes, cantinas, hotéis, eventos e hospitais; montam e desmontam praças, carrinhos, mesas, balcões e bares; organizam, conferem e controlam materiais de trabalho, bebidas e alimentos, listas de espera, a limpeza e higiene e a segurança do local de trabalho; preparam alimentos e bebidas, realizando também serviços de vinhos.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5135", "Trabalhadores auxiliares nos serviços de alimentação", "Os trabalhadores auxiliares nos serviços de alimentaçao auxiliam outros profissionais da área no pré-preparo, preparo e processamento de alimentos, na montagem de pratos. Verificam a qualidade dos gêneros alimentícios, minimizando riscos de contaminaçao. Trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, higiene e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5136", "Churrasqueiros, pizzaiolos e sushimen", "Os profissionais desta fo atuam no setor de serviços de alimentaçao, manipulando alimentos in natura para processá-los. Verificam a qualidade dos alimentos, observam normas técnicas de higiene e segurança para minimizar riscos de contaminaçao e controlam desperdícios.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5141", "Trabalhadores nos serviços de administração de edifícios", "Zelam pela segurança das pessoas e do patrimônio de edifícios de apartamentos, edifícios comerciais, igrejas e outros. Atendem e controlam a movimentação de pessoas e veículos no estacionamento; recebem objetos, mercadorias, materiais, equipamentos; conduzem o elevador, realizam pequenos reparos. Prestam assistência aos religiosos, ornamentam a igreja e preparam vestes litúrgicas.", "N", "202308", "01/2017A", "2");
