INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5143", "Trabalhadores nos serviços de manutenção de edificações", "Executam serviços de manutençao elétrica, mecânica, hidráulica, carpintaria e alvenaria, substituindo, trocando, limpando, reparando e instalando peças, componentes e equipamentos. Conservam vidros e fachadas, limpam recintos e acessórios e tratam de piscinas. Trabalham seguindo normas de segurança, higiene, qualidade e proteçao ao meio ambiente.", "N", "202308", "07/2022A", "2");
