INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5161", "Trabalhadores nos serviços de embelezamento e higiene", "Tratam da estética e saúde e aplicam produtos químicos para ondular, alisar ou colorir os cabelos; cuidam da beleza das mãos e pés; realizam depilação e tratamento de pele;fazem maquiagens sociais e para caracterizações (maquiagem artística); realizam massagens estéticas utilizando produtos e aparelhagem; selecionam, preparam e cuidam do local e materiais de trabalho. Podem administrar os negócios.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5162", "Cuidadores de crianças, jovens, adultos e idosos", "Cuidam de bebês, crianças, jovens, adultos e idosos, a partir de objetivos estabelecidos por instituições especializadas ou responsáveis diretos, zelando pelo bem-estar, saúde, alimentação, higiene pessoal, educação, cultura, recreação e lazer da pessoa assistida.", "N", "202308", "01/2017A", "2");
