INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5166", "Trabalhadores auxiliares dos serviços funerários", "Auxiliam nos serviços funerários, constroem, preparam, limpam, abrem e fecham sepulturas. Realizam sepultamento, exumam e cremam cadáveres, trasladam corpos e despojos. Conservam cemitérios, máquinas e ferramentas de trabalho. zelam pela segurança do cemitério.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5167", "Astrólogos e numerólogos", "Orientam pessoas, organizações privadas ou públicas; fazem previsões com base na interpretação de configurações astrológicas ou numerológicas. Pesquisam e elegem momentos e locais precisos para diversos objetivos. Podem ministrar cursos, dar consultoria e atuar nos meios de comunicação.", "N", "202308", "01/2017A", "2");
