INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5171", "Bombeiros, salva-vidas e afins", "Previnem situações de risco e executam salvamentos terrestres, aquáticos e em altura, protegendo pessoas e patrimônios de incêndios, explosões, vazamentos, afogamentos ou qualquer outra situação de emergência, com o objetivo de salvar e resgatar vidas; prestam primeiros socorros, verificando o estado da vítima para realizar o procedimento adequado; realizam cursos e campanhas educativas, formando e treinando equipes,brigadas e corpo voluntário de emergência.", "N", "202308", "07/2022A", "2");
