INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5191", "Motociclistas e ciclistas de entregas rápidas", "Coletam e entregam documentos, valores, mercadorias e encomendas. Realizam serviços de pagamento e cobrança, roteirizam entregas e coletas. Localizam e conferem destinatários e endereços, emitem e coletam recibos do material transportado. Preenchem protocolos, conduzem e consertam veículos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5192", "Trabalhadores da coleta e seleção de material reciclável", "Catam, selecionam e vendem materiais recicláveis como papel, papelão e vidro, bem como materiais ferrosos e não ferrosos e outros materiais reaproveitáveis.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5193", "Trabalhadores de serviços veterinários, de higiene e estética de animais domésticos", "Realizam procedimentos de enfermagem veterinária. Preparam animais e materiais para procedimentos veterinários. Tosam, banham e enfeitam animais. Limpamouvidos, dentes e olhos de animais. Atendem a clientes-proprietários dos animais e administram o local de trabalho. Trabalham em conformidade a normas e procedimentos de segurança, higiene e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5198", "Profissionais do sexo", "Batalham programas sexuais em locais privados, vias públicas e garimpos; atendem e acompanham clientes homens e mulheres, de orientações sexuais diversas; administram orçamentos individuais e familiares; promovem a organização da categoria. Realizam ações educativas no campo da sexualidade; propagandeiam os serviços prestados. As atividades são exercidas seguindo normas e procedimentos que minimizam as vulnerabilidades da profissão.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5199", "Outros trabalhadores dos serviços", "Prestam serviços diversos a empresas e pessoas. Instalam painéis e cartazes, limpam e guardam veículos; lavam vidros e outros utensílios; combatem pragas. Fazem a leitura e inspeção de medidores e instalações; engraxam artigos de couro. Recolhem bolas durante a prática de esportes (tênis, vôlei etc.); recepcionam espectadores em casas de espetáculos, cinemas, teatros e outros locais de diversão.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5201", "Supervisores de vendas e de prestação de serviços", "Planejam vendas, atendem clientes e coletam indicadores do mercado consumidor. Supervisionam rotina de equipe de vendas. Recrutam, treinam e avaliam profissionais de vendas de produtos e serviços. Apresentam à gerência os resultados das metas de vendas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5211", "Operadores do comércio em lojas e mercados", "Vendem mercadorias em estabelecimentos do comércio varejista ou atacadista, auxiliando os clientes na escolha. Registram entrada e saída de mercadorias. Promovem a venda de mercadorias, demonstrando seu funcionamento, oferecendo-as para degustação ou distribuindo amostras das mesmas. Informam sobre suas qualidades e vantagens de aquisição. Expoem mercadorias de forma atrativa, em pontos estratégicos de vendas, cometiquetas de preço. Prestam serviços aos clientes, tais como: troca de mercadorias;", "N", "202308", "01/2017A", "2");
