INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5241", "Vendedores em domicílio", "Vendem produtos e serviços em residências e escritórios; planejam e discutem metas e estratégias de venda. Contatam, visitam e entrevistam clientes; demonstram produtos, avaliam o perfil dos clientes e fecham contratos de vendas. Orientam, informam e visitam clientes no pós-venda; acompanham entrega de produtos, requisitam manutenção de produtos. Relacionam-se com setores da empresa.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5242", "Vendedores em bancas, quiosques e barracas", "Vendem mercadorias nas vias públicas,em pontos fixos, sob permissão governamental; compram e preparam mercadorias para venda; organizam o local de trabalho, dispondo as mercadorias em feiras livres, bancas, quiosques e barracas, para atender os compradores que procuram esse tipo de mercado. Comunicam-se, apregoando a qualidade e o preço do produto.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("5243", "Vendedores ambulantes", "Vendem mercadorias em vias e logradouros públicos. Estipulam prazos e condições de pagamento e fornecem descontos nos preços. Planejam atividades de vendas e definem itinerários. Compram, preparam e transportam mercadorias para venda, visitam fornecedores, fazem levantamento de preços e negociam preços e condições de pagamentos. Providenciam licença para exercer a ocupação.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6110", "Produtores agropecuários em geral", "Plantam culturas e criam animais. Montam infra-estrutura e administram propriedade agropecuária. Gerenciam recursos humanos. Beneficiam e comercializam produtos de origem vegetal e animal.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6120", "Produtores agrícolas polivalentes", "Planejam e administram unidade de produção. Preparam solo, plantam culturas e realizam tratos culturais. Colhem e comercializam produtos agrícolas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6121", "Produtores agrícolas na cultura de gramíneas", "Plantam e colhem grãos, colmos e plantas tais como arroz, trigo, milho, cevada, sorgo, cana-de-açúcar, aveia e triticale; condicioname fertilizam o solo, manejam plantas invasoras, pragas e doenças em lavoura. Comercializam e administram a produção e a qualidade do armazenamento dos grãos, feno e silagem. Providenciam consultoria técnica, documentos legais e manutenção de benfeitoria, máquinas e implementos. Instalam infra-estrutura de irrigação e drenagem e monitoram volume e distribuição de ág", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6122", "Produtores agrícolas na cultura de plantas fibrosas", "Administram e comercializam a produção de plantas fibrosastais como: algodão, curauá, juta, rami e sisal;preparam solo e executam o plantio; realizam atividades de colheita e armazenamento, tratos culturais e controles fitossanitários. Efetuam reparos e manutenção em máquinas e equipamentos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6123", "Produtores agrícolas na olericultura", "Planejam a logística, gerenciam, comercializam eproduzem legumes, talos, folhas, raízes, bulbos, tubérculos; preparam local para plantio e plantam mudas e sementes; controlam pragas e doenças e efetuam tratos culturais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6124", "Produtores agrícolas no cultivo de flores e plantas ornamentais", "Propagam e definem espécies, variedades e local de produção de flores e plantas ornamentais;realizam manejo,trato culturale tratamentos fitossanitários da produção; preparam solo e substratos para plantio; colhem, supervisionam o trabalho de colheita e armazenagem; implantam infra-estrutura e comercializam a produção. Podem administrar recursos financeiros e pessoal empregado.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6125", "Produtores agrícolas na fruticultura", "Administram propriedade agrícola e planejam atividades da fruticultura. Plantam árvores frutíferas, espécies rasteiras e trepadeiras e realizam tratos culturais. Preparam solo para plantio, comercializam e beneficiam a produção frutífera e desenvolvem atividades de preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6126", "Produtores agrícolas na cultura de plantas estimulantes", "Administram a propriedade agrícola; cultivam plantas estimulantes tais como: café, cacau, erva-mate, guaraná e fumo; plantam mudas; colhem, beneficiam e comercializam a produção. Condicionam terreno para plantio e desenvolvem atividades de preservação de meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6127", "Produtores agrícolas na cultura de plantas oleaginosas", "Selecionam a área, preparam o solo, produzem mudas e sementes, plantam, realizam tratos culturais e beneficiam a colheita de oleaginosas tais como amendoim, canola, colza, coco-da-baía, dendê, palma, girassol, linhaça, linho, mamona e soja. Administram e programam a produção.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6128", "Produtores de especiarias e de plantas aromáticas e medicinais", "Administram propriedades agrícolas produtoras de especiarias, tais como açafrão, alho, anis, baunilha, canela, cardamomo, cogumelo seco, cominho, cravo-da-índia, gengibre, louro, mostarda, noz-moscada, páprica, pimenta-do-reino, pimenta da jamaica etc. E de plantas aromáticas e medicinais como alecrim, babosa, calêndula, hortela, camomila, poejo, porangaba etc. Planejam e preparam área de plantio;cultivam,colhem, beneficiam e comercializam a produção. Produzem mudas e sementes.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6130", "Produtores em pecuária polivalente", "Manejam animais domésticos, como caes e gatos, ou animais da pecuária de pequeno, médio e grande porte, como aves, suinos, ovinos, caprinos, eqüinos, muares e bovinos. Cuidam da alimentação e monitoram a saúde dos animais;organizam a reprodução e controlam a criação; cultivam alimentos para os animais; preparam animais para eventos e os comercializam. Beneficiam e comercializam produtos derivados da pecuária. Higienizam instalações e equipamentos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6131", "Produtores em pecuária de animais de grande porte", "Supervisionam e planejam o manejo e a alimentação do rebanho. Controlam sanidade e organizam a reproduçãodos animais; condicionam bovídeos e eqüídeos; beneficiam e comercializam rebanho e produtos derivados e administram a propriedade rural.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6132", "Produtores em pecuária de animais de médio porte", "Manejam a criação extensiva e confinada de suínos, caprinos e ovinos; gerenciam o empreendimento e comercializam a produção;controlam a produção e a qualidade de animais e produtos derivados;qualificam a mão-de-obra;projetam e implantam criatórios, beneficiam produtos derivados. Preservam meio ambiente e defendem políticas da atividade.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6133", "Produtores da avicultura e cunicultura", "Coordenam manejo da produção de aves, ovos e coelhos;provêem alimentos para aves e coelhos, alimentam e controlam a sanidade dos animais. Providenciam documentos e preparam aves, ovos, coelhos e seus derivados para comercialização. Programam logística de transporte, de insumos e produção, e administram recursos humanos e financeiros da granja. Podem implantar granjas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6134", "Produtores de animais e insetos úteis", "Manejam criação e produção e efetuam colheita de derivados de animais e insetos úteis, tais como: abelha, bicho-da-seda, minhoca e animais produtores de veneno, para produção de cera, mel, casulo, húmus, substâncias venenosas para vacinas etc. Produzem alimentos e controlam pragas e doenças; montam instalações, administram e comercializam produção oriunda dos referidos animais e insetos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6201", "Supervisores na exploração agropecuária", "Supervisionam diretamente uma equipe de trabalhadores agropecuários em sua lida no campo, na alimentação, reprodução e reposição de animais e nos tratos culturais; administram mão-de-obra e treinam a equipe de trabalho;planejam atividades e controlam qualidade e produtividade agropecuária; negociam insumos, produtos e equipamentos agropecuários e realizam manutenção em equipamentos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6210", "Trabalhadores agropecuários em geral", "Tratam animais da pecuária e cuidam da sua reprodução. Preparam solo para plantio e manejam área de cultivo. Efetuam manutenção na propriedade. Beneficiam e organizam produtos agropecuários para comercialização. Classificam-se nessa epígrafe somente os que trabalham em ambas atividades - agrícolas e da pecuária.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6220", "Trabalhadores de apoio à agricultura", "Colhem policulturas, derriçando café, retirando pés de feijão, leguminosas e tuberosas, batendo feixes de cereais e sementes de flores, bem como cortando a cana. Plantam culturas diversas, introduzindo sementes e mudas em solo, forrando e adubando-as com cobertura vegetal. Cuidam de propriedades rurais. Efetuam preparo de mudas e sementes através da construção de viveiros e canteiros, cujas atividades baseiam-se no transplante e enxertia de espécies vegetais. Realizam tratos culturais, além de p", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6221", "Trabalhadores agrícolas na cultura de gramíneas", "Plantam e colhem gramíneas. Preparam sementes, mudas e insumos, condicionando o solo para tratamento de cultura. Realizam atividades de armazenamento e beneficiamento da colheita, como moagem, secagem e classificação dos grãos. Executam manutenção de máquinas e equipamentos agrícolas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6222", "Trabalhadores agrícolas na cultura de plantas fibrosas", "Realizam atividades de colheita, plantam e tratam culturas de plantas fibrosas como o algodão, o sisal e o rami. Classificam as fibras. Preparam o solo. Realizam reparos e manutenção de máquinas e equipamentos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6223", "Trabalhadores agrícolas na olericultura", "Plantam mudas e sementes de feijão, lentilha, ervilha, tomate, beterraba, batatas doce e inglesa, cebola, mandioca, legumes e hortaliças. Produzem mudas e sementes, preparam solo para plantio; irrigam o solo, adubam e aplicam defensivos agrícolas nas covas, mudas e sementes. Manejam área de cultivo, colhem, embalam, armazenam e comercializam os produtos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6224", "Trabalhadores agrícolas no cultivo de flores e plantas ornamentais", "Plantam mudas, sementes, bulbos, rizomas e estacas; manejam o cultivo, colhem e acondicionam paracomercialização de flores, folhagens e plantas ornamentais. Constroem estufas e telas de sombreamento e preparam local para plantio. As atividades são realizadas em conformidade a normas técnicas, de qualidade, de segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6225", "Trabalhadores agrícolas na fruticultura", "Realizam tratos culturais em fruticultura;preparam solo e plantam espécies frutíferas; produzem mudas e sementes. Colhem, beneficiam e acondicionam frutas e frutos. Auxiliam na irrigação das plantações.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6226", "Trabalhadores agrícolas nas culturas de plantas estimulantes", "Colhem folha, ramo e fruto de plantas estimulantes, tais como cacau, café, erva-mate, guaraná e fumo; plantam culturas de plantas estimulantes; produzem mudas de plantas. Beneficiam frutos e folhas de plantas; acondicionam colheita e realizam tratos culturais em plantações. Organizam instalações e equipamentos agrícolas e preparam solo para plantio.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6227", "Trabalhadores agrícolas na cultura de plantas oleaginosas", "Plantam e tratam culturas oleaginosas como amendoim, coco-da-baía, dendê, mamona, soja, girassol e linho. Produzem mudas e sementes, colhem os frutos, preparam o solo, beneficiam e armazenam a colheita.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6228", "Trabalhadores agrícolas da cultura de especiarias e de plantas aromáticas e medicinais", "Executam plantio e produzem mudas e sementesde especiarias, plantas aromáticas e medicinais e colhem e beneficiam sua produção. Preparam o solo para plantio e realizam embalagem e armazenamento de produção. Empregam medidas de segurança e preservação ambiental e participam de eventos agrícolas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6230", "Tratadores polivalentes de animais", "Manejam, alimentam e monitoram a saúde e o comportamento de animais da pecuária. Condicionam e adestram animais. Sob orientação de veterinários e técnicos, tratam sanidade de animais, manipulando e aplicando medicamentos e vacinas, higienizam animais e recintos; aplicam técnicas de inseminação e castração. Realizam atividades de apoio, assessorando em intervenções cirúrgicas, exames clínicos e radiológicos, pesquisas, necropsias e sacrifícios de animais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6231", "Trabalhadores na pecuária de animais de grande porte", "Alimentam e manejam bovinos, bubalinos, eqüinos, asininos e muares, na pecuária de animais de grande porte; ordenham bovídeos. Sob orientação de veterinários e técnicos, cuidam da saúde dos animais e auxiliam na reprodução de animais. Treinam e preparam animais para eventos. Efetuam manutenção de instalações. Realizam tratos culturais em forrageiras, pasto e outras plantações para ração animal.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6232", "Trabalhadores na pecuária de animais de médio porte", "Cuidam da alimentação, gestação e lactação de suínos, caprinos e ovinos. Aplicam medicamentos e fazem curativos. Controlam a reprodução, ordenham, abatem e preparam suínos, caprinos e ovinos para exposição e venda. Beneficiam produtos da pecuária de médio porte. As atividades são desempenhadas em conformidade com normas e procedimentos técnicos, de qualidade e biossegurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6233", "Trabalhadores na avicultura e cunicultura", "Preparam e higienizam instalações e equipamentos utilizados na criação; selecionam,manejam aves e coelhos e controlam sua sanidade;classificam e incubam ovos e realizam pequenas manutenções em instalações e equipamentos de aviário e coelhário.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6234", "Trabalhadores na criação de insetos e animais úteis", "Manejam animais e insetos, tais como abelha, bicho-da-seda, minhoca e animais produtores de veneno;extraem produtos de animais e insetos;providenciam alimentação para animais e insetos;classificam animais, insetos e seus produtos. Controlam pragas e doenças e preparam instalações e materiais de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6301", "Supervisores na área florestal e aqüicultura", "Supervisionam diretamente uma equipe de trabalhadores no manejo e produção florestal e de aqüicultura, naprodução de mudas, implantação e manutenção de florestas, desova, de pesca e alimentação da criação. Administram mão-de-obra, selecionando, contratando e demitindo pessoal, distribuindo tarefas, efetuando pagamento e orientando funcionários. Planejam atividades e controlam produção da área florestal e de aqüicultura; treinam equipes de trabalho e providenciam manutenção de equipamentos e elab", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6310", "Pescadores polivalentes", "Realizam pesca artesanal e captura de crustáceos (exceto camarão e lagosta). Despescam rede e espinhel, possibilitando o preparo e a comercialização do pescado. Constroem, mantêm e conduzem embarcações de pequeno porte.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6311", "Pescadores profissionais artesanais de água doce", "Capturam diversos tipos de pescado de água doce, de acordo com regulamentação regional e federal, preservando matas ciliares eambiente aquático. Providenciam documentação de pesca, aprontam e conduzem embarcações, planejam pesca e preparammaterial para sua efetivação. Realizam despesca. Beneficiam e comercializam pescado.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6312", "Pescadores de água costeira e alto mar", "Capturam, despescam e beneficiam animais aquáticos. Preparam e limpam embarcação e equipamentos de pesca. Carregam e descarregam embarcação e auxiliam em serviços gerais de navegação.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6313", "Criadores de animais aquáticos", "Manejam e alimentam animais aquáticos. Controlam ambiente aquático,monitoram e constroem instalações aquáticas. Organizam reprodução de animais aquáticos e cuidam de sua sanidade. Planejam criação, beneficiam e comercializam animais aquáticos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6314", "Trabalhadores de apoio à pesca", "Preparam equipamentos de pesca, conservam pescado e controlam urnas e câmaras de resfriamento. Confeccionam material de pesca. Auxiliam tripulação em serviços gerais e carregam e descarregam embarcação. Realizam serviços de manutenção de embarcações de pesca em estaleiros.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6320", "Trabalhadores florestais polivalentes", "Manejam recursos naturais. Produzem mudas, realizam manutenção de plantas e manipulam plantas medicinais. Guiam pessoas em florestas e campos e disponibilizam serviços e produtos. Trabalham seguindo normas de segurança, higiene e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6321", "Extrativistas e reflorestadores de espécies produtoras de madeira", "Extraem madeira, identificando áreas de extração, derrubando árvores mapeadas, classificando toras conforme diâmetro e comprimento e separando madeira de acordo com sua utilização. Reflorestam áreas, apanhando sementes em árvores e brotos para clonagem e plantando mudas de árvores. Inventariam florestas, identificando espécies, monitorando crescimento de árvores e levantando potencial de madeira em florestas renováveis e nativas. Realizam medições ao cubar árvores derrubadas. Transportam árvores", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6322", "Extrativistas florestais de espécies produtoras de gomas e resinas", "Extraem gomas elásticas, não elásticas e resinas, raspando e cortando cascas de árvores, chanfrando e sangrando troncos de árvores. Preparam extração de gomas e resinas. Processam material de extração. Confeccionam instrumentos de trabalho e organizam comercialização de produtos de extração. Manejam área de extração e transportam matéria-prima e produtos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6323", "Extrativistas florestais de espécies produtoras de fibras, ceras e óleos", "Extraem e beneficiam fibras, ceras e óleos. Colhem frutos de palmeiras e árvores. Manejam extração e beneficiamento de fibras, ceras e óleos. Plantam árvores de pequi e espécies produtoras de fibras. Armazenam e comercializam matéria-prima e produtos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6324", "Extrativistas florestais de espécies produtoras de alimentos silvestres", "Plantam espécies produtoras de alimentos silvestres. Extraem, beneficiam, transportam e comercializam alimentos silvestres,como o açaí, a castanha, o pinhão e a pupunha. Manejam área de extração e preparam equipamentos de colheita, plantio e beneficiamento.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6325", "Extrativistas florestais de espécies produtoras de substâncias aromáticas, medicinais e tóxicas", "Extraem cascas, entrecascas, folhas, sementes, flores, raízes, frutos e resinas de espécies florestais produtoras de substâncias aromáticas, medicinais e tóxicas; beneficiam e transportam produtos de extração. Manejam área de extração e estocam produtos de extração e beneficiados. Comercializam produtos beneficiados e preparam equipamentos e acessórios.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6326", "Carvoejadores", "Preparam os fornos para a carbonização, verificando o abastecimento da lenha e as condições de funcionamento dos mesmos. Controlam a carbonização, conferindo os pegadores dos fornos, das filinhas e outros, separando lenhas não carbonizadas do carvão. Constroem os fornos. Instruem trabalhadores sobre segurança no trabalho", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6410", "Trabalhadores da mecanização agrícola", "Operam, ajustam e preparam máquinas e implementos agrícolas. Realizam manutenção em primeiro nível de máquinas e implementos. Empregam medidas de segurança e auxiliam em planejamento de plantio.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6420", "Trabalhadores da mecanização florestal", "Dirigem máquinas pesadas de operação florestal. Preparam atividade de colheita florestal, efetuam derrubada, descasque e desgalhamento mecânico de toras e estocam madeira. Inspecionam máquinas florestais, realizam manutenção em segundo nível de máquinas florestais e empregam medidas de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("6430", "Trabalhadores da irrigação e drenagem", "Controlam processo de irrigação. Verificam e reparam equipamentos de irrigação. Instalam e acionam sistemas de irrigação. Adubam plantação.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7101", "Supervisores da extração mineral", "Supervisionam, coordenam e treinam equipes de trabalhadores da extração mineral e materiais geológicos que atuam na operação de minas, perfuração, instalação, desmonte de material geológico, escavação, carregamento e transporte de material de extração. Desempenham atividades que permitem assegurar o cumprimento de normas de segurança e preservação ambiental, implementam planejamento de produção da mina, gerenciam equipes de trabalho, supervisionam operação de apoio da mina, coordenam atividades ", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7102", "Supervisores da construção civil", "Supervisionam equipes de trabalhadores da construção civil que atuam em usinas de concreto, canteiros de obras civis e ferrovias. Elaboram documentação técnica e controlam recursos produtivos da obra (arranjos físicos, equipamentos, materiais, insumos e equipes de trabalho). Controlam padrões produtivos da obra tais como inspeção da qualidade dos materiais e insumos utilizados, orientação sobre especificação, fluxo e movimentação dos materiais e sobre medidas de segurança dos locais e equipament", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7111", "Trabalhadores da extração de minerais sólidos", "Pesquisam subsolo da jazida e retiram amostras de minerais sólidos, carvão e outros tipos de rochas, pedras preciosas e semipreciosas da superfície e do interior de minas, pedreiras, terra firme, barrancos e leitos de rios, por meio de furos de sondagem. Inspecionam frentes de trabalho para operação de equipamentos. Instalam cavilhas e chumbadores nos tetos ou paredes da galeria (mina subterrânea). Realizam desmonte mecânico, hidráulico e manual de rochas e controlam o transporte e o tráfego de ", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7112", "Trabalhadores de extração de minerais sólidos (operadores de máquinas)", "Operam equipamentos de perfuração e de corte de rochas, equipamentos de escavação e carregamento de minérios e equipamentos de transporte de cargas. Inspecionam as condições operacionais dos equipamentos e preparam o local de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7113", "Trabalhadores da extração de minerais líquidos e gasosos", "Realizam pesquisa sísmica, perfuram poços em terra e mar. Avaliam área perfurada; extraem minerais líquidos e gasosos; realizam o processamento primário de separação de óleo, água e gás. Para a realização das atividades são utilizadas e emitidas informações orais e escritas, em conformidade a normas e procedimentos técnicos, de segurança, meio ambiente e saúde. Podem ministrar treinamentos no local de trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7114", "Garimpeiros e operadores de salinas", "Pesquisame preparam áreas para extração de minérios; providenciam máquinas, equipamentos, ferramentas e acessórios de segurança; exploram mina,beneficiam o minério e comercializam o minério. Preparam área para extração e processam o sal, controlam a quantidade de sal da salmoura e extraem o sal.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7121", "Trabalhadores de beneficiamento de minérios", "Inspecionam equipamentos, operam instalação de beneficiamento de minérios e equipamentos de cominuição, de classificação e de concentração. Recuperam água por espessamento. Atuam em conformidade a normas técnicas de qualidade, segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7122", "Trabalhadores de beneficiamento de pedras ornamentais", "Realizam atividades de beneficiamento e ajustagem de pedras. Efetuam acabamento em superfícies de pedra e constroem pisos de granitina. Podem planejar todas as fases do trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7151", "Trabalhadores na operação de máquinas de terraplenagem e fundações", "Planejam o trabalho, realizam manutenção básica de máquinas pesadas e as operam. Removem solo e material orgânico bota-fora, drenam solos e executam construção de aterros. Realizam acabamento em pavimentos e cravam estacas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7152", "Trabalhadores de estruturas de alvenaria", "Organizam e preparam o local de trabalho na obra; constroem fundações e estruturas de alvenaria. Aplicam revestimentos e contrapisos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7153", "Montadores de estruturas de concreto armado", "Preparam a confecção de armações e estruturas de concreto e de corpos de prova. Cortam e dobram ferragens de lajes. Montam e aplicam armações de fundações, pilares e vigas. Moldam corpos de prova.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7154", "Trabalhadores na operação de máquinas de concreto usinado e afins", "Programam a produção e o fornecimento de concreto e misturam seus agregados. Preparam o ambiente, os equipamentos de trabalho e os insumos do concreto. Descarregam e bombeiam o concreto.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7155", "Trabalhadores de montagem de estruturas de madeira, metal e compósitos em obras civis", "Planejam trabalhos de carpintaria, preparam canteiro de obras e montam fôrmas metálicas. Confeccionam fôrmas de madeira e forro de laje (painéis), constroem andaimes e proteção de madeira e estruturas de madeira para telhado. Escoram lajes de pontes, viadutos e grandes vãos. Montam portas e esquadrias. Finalizam serviços tais como desmonte de andaimes, limpeza e lubrificação de fôrmas metálicas, seleção de materiais reutilizáveis, armazenamento de peças e equipamentos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7156", "Trabalhadores de instalações elétricas", "Planejam serviços elétricos, realizam instalação de distribuição de alta e baixa tensão. Montam e reparam instalações elétricas e equipamentos auxiliares em residências, estabelecimentos industriais, comerciais e de serviços. Instalam e reparam equipamentos de iluminação de cenários ou palcos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7157", "Aplicadores de materiais isolantes", "Identificam serviços de instalação de materiais isolantes, selecionam materiais a serem aplicados, solicitam liberação da área de trabalho, preparam o local e executam a instalação de acordo com projetos, normas técnicas, normas de segurança e recomendações dos fabricantes. Providenciam descarte de resíduos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7161", "Revestidores de concreto", "Planejam atividades de concretagem, preparam materiais e equipamentos necessários à concretagem e concretam áreas. Realizam acabamento em superfícies de concreto, revestem e corrigem falhas em superfícies. Dão polimento no concreto.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7162", "Telhadores (revestimentos rígidos)", "Planejam serviços de cobertura de edificações. Confeccionam e montam estruturas de madeira ou metal para cobertura;colocam telhas de argila, amianto, metálica ou plástica. Operam máquinas e ferramentas para madeira ou metal.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7163", "Vidraceiros (revestimentos rígidos)", "Preparam máquinas, equipamentos e instrumentos para corte de vidros. Cortam, montam e instalam vidros, vitrais e espelhos. Temperam vidros e montam vidros temperados. Confeccionam, lapidam e pintam vitrais. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7164", "Gesseiros", "Preparam ferramentas, equipamentos, materiais e selecionam peças de acordo com o projeto de decoração. Fabricam e recompoem placas, peças e superfícies de gesso. Revestem tetos e paredes e rebaixam tetos com placas de painéis e gesso. Realizam decorações com peças de gesso e montam paredes divisórias com blocos e painéis de gesso.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7165", "Aplicadores de revestimentos cerâmicos, pastilhas, pedras e madeiras", "Planejam o trabalho e preparam o local de trabalho. Estabelecem os pontos de referência dos revestimentos e executam revestimentos em paredes, pavimentos, muros e outras partes de edificações com ladrilhos, pastilhas, mármores, granitos, ardósia ou material similar, tacos e tábuas de madeira. Fazem polimento e lustram revestimentos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7166", "Pintores de obras e revestidores de interiores (revestimentos flexíveis)", "Pintam as superfícies externas e internas de edifícios e outras obras civis, raspando-a amassando-as e cobrindo-as com uma ou várias camadas de tinta; revestem tetos, paredes e outras partes de edificações com papel e materiais plásticos e para tanto , entre outras atividades,preparam as superfícies a revestir, combinam materiais etc.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7170", "Ajudantes de obras civis", "Demolem edificações de concreto, de alvenaria e outras estruturas; preparam canteiros de obras, limpando a área e compactando solos. Efetuam manutenção de primeiro nível, limpando máquinas e ferramentas, verificando condições dos equipamentos e reparando eventuais defeitos mecânicos nos mesmos. Realizam escavações e preparam massa de concreto e outros materiais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7201", "Supervisores de usinagem, conformação e tratamento de metais", "Coordenam, orientam e treinam equipes de trabalho de usinagem, conformação e tratamento de metais, nos métodos, processos produtivos e da qualidade. Organizam equipamentos utilizados nos processos de produção, estruturando arranjos físicos e células de trabalho. Monitoram processos de usinagem, conformação e tratamento dos metais. Garantem a programação da produção, dimensionando disponibilidade dos equipamentos e definindo pessoal em função do tipo, da especificação do serviço, das prioridades ", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7202", "Supervisores da fabricação e montagem metalmecânica", "Supervisionam equipes de trabalho na produção e montagem de equipamentos em indústrias da metalmecânica. Elaboram e seguem informações da documentação técnica tais como relatórios, cronogramas de produção, montagem de equipamentos e manuais de operação de equipamentos. Controlam recursos e processos da produção e administram resultados da produção. Desenvolvem novos fornecedores e equipes de trabalho. Prestam assessoria para o estabelecimento de políticas e metas da empresa. Coordenam ações volt", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7211", "Ferramenteiros e afins", "Constróem e desenvolvem ferramentas e dispositivos de usinagem, estampos de corte, dobra, repuxo e corte fino, moldes de sopro, de injeção e eletroerosão, modelos de moldes metálicos para fundição; fazemcontrole dimensional deprodutos e peças usinadas e planejam o processo de construção de produtos ou protótipos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7212", "Preparadores e operadores de máquinas-ferramenta convencionais", "Preparam, regulam e operam máquinas-ferramenta que usinam peças de metal e compósitos e controlam os parâmetros e a qualidade das peças usinadas, aplicando procedimentos de segurançaàs tarefas realizadas. Planejam seqüências de operações, executam cálculos técnicos; podem implementar ações de preservação do meio ambiente. Dependendo da divisão do trabalho na empresa, podem apenas preparar ou operar as máquinas-ferramenta.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7213", "Afiadores e polidores de metais", "Planejam o trabalho de polimento de superfícies metálicas e de afiação de ferramentas. Fazempolimento e afiação utilizando processos manuais, semi-automáticos e automáticos, controlando a qualidade do serviço e aplicandonormas de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7214", "Operadores de máquinas de usinagem cnc", "Usinam peças de metais ferrosos e não-ferrosos. Resinas e plásticos em máquinas cnc; preparam e ajustam máquinas de usinagem cnc. Ajustam ferramentas, realizam testes e controle de ferramental. Documentam atividades tais como preenchimento de fichas de controle de produção, resultados do controle estatístico do processo, referências das peças, atualização dos leiautes de ferramentas e ocorrências de manutenção das máquinas. Trabalham seguindo normas de segurança, higiene, qualidade e preservação", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7221", "Trabalhadores de forjamento de metais", "Preparam matrizes e a linha de produção para forjar peças metálicas, calibrampeças forjadas à frio. Podem reparar peças forjadas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7222", "Trabalhadores de fundição de metais puros e de ligas metálicas", "Preparam panela de vazamento de metal líquido; fundem metais; produzem lingotes de metal; efetuam o vazamento do metal líquido das panelas para os moldes. Produzem peças por processo de centrifugação ou sob pressão e dão acabamento em peças fundidas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7223", "Trabalhadores de moldagem de metais e de ligas metálicas", "Preparam a areia para moldagem e macharia. Confeccionam machos e moldes em processos mecanizado e manual. Confeccionam, a mão e a máquina, moldes de areia para moldagem de metais e machos para fundição de peças ocas. Operam equipamentos de preparação da areia.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7224", "Trabalhadores de trefilação e estiramento de metais puros e ligas metálicas", "Abastecem máquinas e operam equipamentos para trefilar metais, extrudar perfis e produzir cabos metálicos, realizam tratamento térmico e dão acabamento em produtos. Monitoram os processos cumprindo normas e procedimentos técnicos de qualidade, segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7231", "Trabalhadores de tratamento térmico de metais", "Modificam as propriedades físicas de peças de metal por meio de aquecimento, resfriamento e tratamento químico, para temperá-las, cementá-las e normalizá-las. Para tanto, preparam e operam fornos de tratamento térmico e periféricos, montam e desmontam cargas dos fornos, controlam as etapas do processo e monitoram os seus parâmetros, inspecionam as peças submetidas a tratamento térmico. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7232", "Trabalhadores de tratamento de superfícies de metais e de compósitos (termoquímicos)", "Tratam superfícies de peças metálicas e não-metálicas ou de material sintético por processos mecânicos, decapagem, pintura, fosfatização, galvanização por cromeação, niquelação, zincagem e outras, paraproteger as peças contra corrosão ou para lhes dar acabamento técnico ou decorativo. Realizam manutenção de banhos de galvanoplastia e anodização. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7233", "Trabalhadores da pintura de equipamentos, veículos, estruturas metálicas e de compósitos", "Analisam e preparam as superfícies a serem pintadas e calculam quantidade de materiais para pintura. Identificam, preparam e aplicam tintas em superfícies, dão polimento e retocam superfícies pintadas. Secam superfícies e reparam equipamentos de pintura.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7241", "Encanadores e instaladores de tubulações", "Operacionalizam projetos de instalações de tubulações, definem traçados e dimensionam tubulações; especificam, quantificam e inspecionam materiais; preparam locais para instalações, realizam pré-montagem e instalam tubulações. Realizam testes operacionais de pressão de fluidos e testes de estanqueidade. Protegem instalações e fazem manutenções em equipamentos e acessórios.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7242", "Trabalhadores de traçagem e montagem de estruturas metálicas e de compósitos", "Confeccionam gabaritos e modelos de peças de estruturas metálicas diversas, incluindo estruturas de embarcações e aeronaves; preparam peças da estrutura;montam, instalam e recuperam estruturas metálicas. Realizam manutenção produtiva demáquinas e equipamentos. Organizam o local de trabalho para executá-lo conforme normas e procedimentos técnicos, de qualidade, segurança e preservação do meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7243", "Trabalhadores de soldagem e corte de ligas metálicas", "Unem e cortam peçasde ligas metálicas usando processos de soldageme cortetais como eletrodo revestido, tig, mig, mag, oxigás, arco submerso, brasagem, plasma. Preparam equipamentos, acessórios, consumíveisde soldagem e corte e peças a serem soldadas. Aplicam estritas normas de segurança, organização do local de trabalho e meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7244", "Trabalhadores de caldeiraria e serralheria", "Confeccionam, reparam e instalam peças e elementos diversos em chapas de metal como aço, ferro galvanizado, cobre, estanho, latão, alumínio e zinco; fabricam ou reparam caldeiras, tanques, reservatórios e outros recipientes de chapas de aço; recortam, modelam e trabalham barras perfiladas de materiais ferrosos e não ferrosos para fabricar esquadrias, portas, grades, vitrais e peças similares.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7245", "Operadores de máquinas de conformação de metais", "Dobram chapas e barras metálicas. Curvam tubos, chapas e barras de metais. Conformam peças de metais por prensagem hidráulica e excêntrica. Cortam chapas de metais. Controlam a qualidade de chapas, barras e tubos de metais. Realizam manutenção de máquinas e matrizes.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7246", "Trançadores e laceiros de cabos de aço", "Confeccionam cabos e laços de cabos de aço. Regulam máquinas de cablear, soldar e prensar, segundo padrões estabelecidos. Inspecionam a qualidade de cabos e laços de cabo de aço. Alimentam máquinas cableadoras. Repassam cabos de aço, arame, alma e perna. Soquetam cabos de aço e realizam manutenção de equipamentos. Registram informações técnicas e operacionais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7250", "Ajustadores mecânicos polivalentes e afins", "Planejam e organizam o local de trabalho para execução de atividades de ajustagem mecânica. Fabricam, reparam, realizam manutenção e instalam peças e equipamentos, segundo normas de qualidade e segurança do trabalho. Calibram instrumentos de medição e traçagem.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7251", "Montadores de máquinas, aparelhos e acessórios em linhas de montagem", "Montam peças para máquinas e aparelhos e acessórios em linha de montagem. Organizam o local e revisam instruções de trabalho. Fazem manutenção preventiva em ferramentas. Confeccionam e especificam peças de montagem. Montam máquinas e aparelhos eletrônicos. Preenchem relatórios, notas, requisições e laudos técnicos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7252", "Montadores de máquinas industriais", "Montam e desmontam máquinas industriais, operam instrumentos de medição mecânica, ajustam peças mecânicas, lubrificam, expedem e instalam máquinas, realizam manutenções corretivas e prestam assistência técnica-mecânica de máquinas industriais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7253", "Montadores de máquinas pesadas e equipamentos agrícolas", "Montam máquinas agrícolas, de terraplenagem e perfuratrizes, inspecionando e fixando peças e conjuntos mecânicos e abastecendo sistemas hidráulicos, de arrefecimento e de lubrificação. Montam equipamentos de levantamento, movimentação e descarga, avaliando condições de locais para instalações, realizando terraplanagem para instalação e montagem, montando e alinhando bases e rampas de plataformas de descarga. Montam equipamentos de extração e beneficiamento de minérios e rochas, tais como aliment", "N", "202308", "01/2017A", "2");
