INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7256", "Montadores de sistemas e estruturas de aeronaves", "Montam sistemas e estruturas de aeronaves; montam sistemas elétricos e equipamentos eletrônicos; lêem e interpretam documentação técnica. Fabricam componentes de estruturas das aeronaves. Instalam sistemas elétricos e equipamentos eletrônicos, sistemas hidráulicos, de trens de pouso, de comando de vôo, de combustível, de oxigênio, pneumáticos, de ar condicionado e de pressurização, sistemas moto-propulsores e unidades auxiliares de partida, sistemas de hélice, rotor e instrumentos de controle de", "N", "202308", "01/2017A", "2");
