INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7301", "Supervisores de montagens e instalações eletroeletrônicas", "Planejam, organizam e monitoram as atividades de montagens e instalações eletroeletrônicas; supervisionam e orientam diretamente equipe de trabalhadores de chão-de-fábrica. Controlam recursos de produção e redigem documentos técnicos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7311", "Montadores de equipamentos eletroeletrônicos", "Montam, testam e inspecionam placas, aparelhos e, ou equipamentos eletroeletrônicos. Instalam painel de comando de rampa, esteira rolante e elevadores; preenchem relatórios e fichas dos equipamentos. Organizam e mantêm o local de trabalho em condições de uso. Abastecem o posto de trabalho de componentes, peças e materiais.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7312", "Montadores de aparelhos de telecomunicações", "Instalam estações de rádio e tv e equipamentos de radar (torre, antena e acessórios). Elaboram o plano de manutenção e realizam manutenções corretiva, periódica e preventiva em equipamentos, peças e componentes. Participam da elaboração dos projetos de infra-estrutura e técnico de estações de rádio e tv. Orientam equipes de operadores e elaboram documentação técnica (relatórios, manuais de procedimentos, escalas de serviços, quadro de controle de manutenção e outras). Trabalham seguindo normas d", "N", "202308", "01/2017A", "2");
