INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7618", "Inspetores e revisores de produção têxtil", "Classificam bobinas de fios têxteis, tecidos planos e de malhas e preparam lotes de produção conforme programação pré-estabelecida. Empregam ações preventivas e corretivas na produção de fios têxteis, tecidos planos e de malhas e registram dados para controle estatístico e de qualidade. Identificam necessidades de treinamento. Trabalham em conformidade a normas e procedimentos técnicos de qualidade, segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
