INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7622", "Trabalhadores do curtimento de couros e peles", "Controlam parâmetros físico-químicos e operam o processo de curtimento de peles e couros. Classificam couros (flor e raspa) e operam máquinas para enxugamento e rebaixamento de peles e couros. Trabalham seguindo normas e procedimentos técnicos e de qualidade, segurança, meio ambiente, higiene e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7623", "Trabalhadores do acabamento de couros e peles", "Recurtem couros e controlam processos e operações do acabamento de couros e peles. Operam máquinas e equipamentos de acabamento de couros e peles. Pré-acabam, acabam e expedem couros e peles. Trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, meio ambiente, higiene e saúde.", "N", "202308", "01/2017A", "2");
