INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7640", "Trabalhadores polivalentes da confecção de calçados", "Atuam em todas as etapas da produção de calçados, desde a preparação do corte até a expedição. Preparam materiais, cortam e preparam peças e solados; pespontam peças, montam, realizam acabamentos e preparam a expedição de calçados, seguindo normas e procedimentos técnicos, de qualidade, segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7641", "Trabalhadores da preparação da confecção de calçados", "Organizam o corte de peças para a confecção de calçados, cortam as peças. Preparam peças da parte superior do calçado. Confeccionam solas para calçados e preparam palmilhas e saltos para a confecção de calçados. Realizam inspeções nos componentes dos calçados. Trabalham em conformidade a normas e procedimentos técnicos, de qualidade, segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7642", "Operadores de máquinas de costurar e montar calçados", "Preparam máquinas e peças para costurar calçados; costuram peças para fabricação de calçados; prepararam máquinas e peças para montagem de calçados e montam calçados. Mantêm máquinas e equipamentos em condições de uso.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7643", "Trabalhadores de acabamento de calçados", "Colocam solados, fixam saltos e palmilham calçados, limpam e lustram calçados. Revisam numeração, tonalidade, costuras e colagem de calçados, registrando ocorrências de falhas e defeitos. Preparam calçados para expedição.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7650", "Trabalhadores polivalentes da confecção de artefatos de tecidos e couros", "Confeccionam moldes (fôrmas, facas) e modelos de artefatos de tecido, couro e similares. Preparam e cortam materiais e peças para confecção. Realizam pintura e adornos em artefatos,atividades de acabamento e serviços de manutenção em máquinas e equipamentos. Montam e embalam artefatos e comercializam produtos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7651", "Trabalhadores da preparação de artefatos de tecidos, couros e tapeçaria", "Preparam materiais, equipamentos e ferramentas para a confecção de artefatos de tecidos e couros. Preparam tecidos, couros e tapetes para o corte e cortam - a mão e a máquina - tecidos couros e tapetes. Realizam testes e inspeções e compoem conjuntos e lotes de peças de tecidos, couros e tapetes. Preparam máquinas para corte de tecidos, couros e tapetes e realizam manutenção produtiva de máquinas, equipamentos e ferramentas. Trabalham seguindo normas de segurança, higiene, saúde, qualidade e pro", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7652", "Trabalhadores da confecção de artefatos de tecidos, couros e sintéticos", "Planejam a confecção e a instalação de artefatos de tecido e couro. Confeccionam moldes e cortam materiais. Preparam materiais para a montagem e montam artefatos de tecido e couro. Realizam acabamentos e revisam artefatos de tecido e couro. Efetuam manutenção produtiva de máquinas e equipamentos. Trabalham seguindo normas de segurança, higiene, saúde, qualidade e proteção ao meio ambiente.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7653", "Operadores de máquinas na confecção de artefatos de couro", "Organizam máquinas na fabricação de tecidos e couros. Preparam as máquinas, interpretando ordem técnica e ficha técnica de produção e definindo seqüência de operações de montagem. Recebem materiais para costura e montagem de artefatos. Preparam peças para montagem e montam artefatos. Costuram e dão acabamento em artefatos. Trabalham em conformidade a normas e procedimentos técnicosde qualidade, segurança, meio ambiente, higiene e saúde.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7654", "Trabalhadores do acabamento de artefatos de tecidos e couros", "Realizam testes e inspeções em artefatos de tecido e couro. Decoram, dão acabamento e preparam os artefatos para a expedição. Embalam produtos acabados. Trabalham em conformidade a normas e procedimentos técnicosde qualidade, segurança, meio ambiente, higiene e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7661", "Trabalhadores da pré-impressão gráfica", "Planejam serviços de pré-impressão gráfica. Realizam programação visual gráfica e editoram textos e imagens. Operam processos de tratamento de imagem, montam fotolitos e imposição eletrônica. Operam sistemas de prova e copiam chapas. Gravam matrizes para rotogravura, flexografia, calcografia e serigrafia. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7662", "Trabalhadores da impressão gráfica", "Planejam serviços da impressão gráfica e ajustam máquinas para impressão. Realizam serviços da impressão gráfica, tais como impressão plana e rotativa, impressão digital, flexografia, litografia, tipografia, letterset, calcografia, tampografia, rotogragura e serigrafia (silkscreen). Trabalham seguindo normas e procedimentos técnicos e de qualidade, segurança, meio ambiente, higiene e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7663", "Trabalhadores do acabamento gráfico", "Planejam a execução do serviço, ajustam e operam máquinas de acabamento gráfico e editorial. Preparam matrizes de corte e vinco, fazem gravações a máquina (hot-stamping) e realizam manutenção produtiva dos equipamentos. Trabalham em conformidade a estritas normas e procedimentos técnicos, de qualidade, segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7664", "Trabalhadores de laboratório fotográfico e radiológico", "Ampliam fotografias coloridas e preto em branco. Revelam filmes coloridos, preto em branco, dispositivos e negativos. Conferem fotos ampliadas e reproduzem fotografias. Preparam equipamentos e soluções químicas de reforço. Manipulam filmes radiológicos e revelam filmes radiográficos. Trabalham seguindo normas de qualidade, segurança, higiene, saúde e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7681", "Trabalhadores de tecelagem manual, tricô, crochê, rendas e afins", "Confeccionam fios, estirando, torcendo e fiando algodões; operam urdideira, entrelaçando e esticando fios. Operam tear manual, unindo, fixando e penteando fios; tecem tecidos, operando pedais e lançadeiras. Confeccionam peças de tricô, crochê, chapéus e redes, definindo tipos, selecionando modelos, confeccionando moldes e amostras e montando pontos na agulha. Preparam palhas, extraindo, selecionando e secando folhas. Comercializam produtos de tecelagem manual.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7682", "Trabalhadores artesanais da confecção de peças e tecidos", "Criam e riscam o desenho para bordá-los. Prendem e cortam tecidos desenhados. Cerzem peças, bordam-nas e dão acabamento. Comercializam bordados e serviços de reparo em tecidos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7683", "Trabalhadores artesanais da confecção de calçados e artefatos de couros e peles", "Cortam, montam e costuram calçados de couro, a mão. Confeccionam bolsas, carteiras, cintos, selas e arreios de couro. Realizam acabamento em calçados e em artefatos de couro.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7686", "Trabalhadores tipográficos linotipistas e afins", "Confeccionam matriz (chapas) para impressão; compoem textos para impressão manual ou por meio de máquinas tipográficas, linotipos, recorte e pintura a pincel. Imprimem trabalhos gráficos, artísticos e publicitários,tais como notas fiscais, outdoors, cartazes, letreirosetc. Confeccionam carimbos; preenchem ordens de serviço e comunicação interna.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7687", "Encadernadores e recuperadores de livros (pequenos lotes ou a unidade)", "Encadernam, douram e gravam manualmente e por meio de diversos processos gráficos e artísticos, folhas, documentos e livros. Realizam impressão hot stamp.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7701", "Supervisores em indústria de madeira, mobiliário e da carpintaria veicular", "Assessoram aquisição de equipamentos, materiais e serviços,coordenam diretamente equipes de trabalhadorese organizam ambiente de trabalho em indústrias da madeira, mobiliário e de carpintaria. Preparam madeiras e supervisionam produção. Controlam estoque e expedição de materiais e produtos e manutenção de máquinas, equipamentos e ferramentas. Proporcionam segurança no trabalho.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7711", "Marceneiros e afins", "Preparam o local de trabalho, ordenando fluxos do processo de produção, e planejam o trabalho, interpretando projetos desenhos e especificações e esboçando o produto conforme solicitação. Confeccionam e restauram produtos de madeira e derivados (produção em série ou sob medida). Entregam produtos confeccionados sob-medida ou restaurados, embalando, transportando e montando o produto no local da instalação em conformidade a normas e procedimentos técnicos e de segurança, qualidade, higiene e pres", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7721", "Trabalhadores de tratamento e preparação da madeira", "Preparam atividades de tratamento e secagem de madeiras, analisando e elaborando programas de secagem. Classificam, tratam e secam madeira. Elaboram documentação técnica tais como relatórios de produção, registros de ocorrências e solicitação de materiais e insumos. Realizam manutenção preventiva de máquinas e equipamentos. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7731", "Operadores de máquinas de desdobramento da madeira", "Planejam operações de desdobramento de madeiras e preparam máquinas para sua realização. Seguem procedimentos de segurança e realizam manutenção de primeiro nível. Desdobram madeiras e controlam qualidade do desdobramento.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7732", "Operadores de máquinas de aglomeração e prensagem de chapas", "Preparam aglomerantes para montagem de chapas de madeira; preparam, operam, realizam pequenos reparos em máquinas de laminação, aglomeração e prensagem de chapas de madeira, de acordo com procedimentos de organização do trabalho, normas técnicas, de segurança e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7733", "Operadores de usinagem convencional de madeira", "Planejam atividades e organizam ambiente de trabalho para usinagem de madeira com máquinas convencionais. Preparam máquinas de usinagem e de beneficiamento de madeira e realizam reparos e ajustes mecânicos e elétricos nas máquinas. Regulam e operam máquinas utilizadas nos diversos tipos de usinagem de madeira em um torno, empregando ferramentas manuais. Trabalham seguindo normas e procedimentos técnicos, de qualidade, segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7734", "Operadores de máquina de usinar madeira (produção em série)", "Programam as atividades para fabricação de peças de madeira e preparam madeira, insumos e máquinas para processamento (produção em série). Operam máquinas de usinar madeira e controlam a qualidade do processamento de peças e de produtos de madeira. Realizam manutenção básica de máquinas e equipamentos e elaboram documentação, registrando informações técnicas e operacionais das atividades realizadas. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7735", "Operadores de máquinas de usinagem de madeira cnc", "Operam, programam e realizam manutenção produtiva de máquinas de usinagem cnc. Interpretam ordens de produção e projetos de produtos. Organizam o ambiente de trabalho conforme normas e procedimentos técnicos, de qualidade, de segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7741", "Montadores de móveis e artefatos de madeira", "Preparam o local de trabalho, montam em série ou a unidade e instalam móveis e artefatos de madeira, caixas, caixotes, paletes, engradados etc. Programam as etapas de montagem, selecionam máquinas, ferramentas e instrumentos, interpretam instruções e executam o trabalho em conformidade as normas e procedimentos técnicos, de qualidade, de segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7751", "Trabalhadores de arte edo acabamento em madeira do mobiliário", "Elaboram projetos de acabamento em madeira e mobiliário, planejam o trabalho, organizam o local de execução, preparam máquinas e ferramentas;preparam as superfícies e realizam o acabamento em madeiras e móveis conforme normas e procedimentos técnicos, de qualidade, de segurança, meio ambiente e saúde. Podem aplicar inúmeros processos e técnicas de acabamento, dentre elas: marchetaria, entalhe, lustração, folheação, laqueação, decapê, marmorização.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7764", "Confeccionadores de artefatos de madeira, móveis de vime e afins", "Fabricam móveis de junco, vime, bambu e rústico, confeccionam cestos e montam vassouras, pincéis e escovas. Preparam fibras para fabricação, controlam a qualidade e realizam serviços de acabamento em artefatos de madeira, móveis de vime e afins. Operam máquinas tais como furadeiras, serras elétricas, lixadeiras. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7771", "Carpinteiros navais", "Modelam fôrmas, preparam quilhas e montam cavernas. Constroem costados ou tabuados, convés, borda-falsa, casaria, porão da embarcação, móveis e seus acessórios e estrutura de lançamento e de docagem. Preparam bases para equipamentos e ferragens. Reparam embarcações. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7772", "Carpinteiros de carrocerias e carretas", "Preparam o trabalho de fabricação, fabricam (constroem), reformam e montam carrocerias, carretas e veículos similares de madeira. Confeccionam peças de madeira para carrocerias e carretas e montam as suas estruturas. Constroem rodas de madeira para carretas e executam a montagem de tampas laterais, traseiras e frontais. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7801", "Supervisores de trabalhadores de embalagem e etiquetagem", "Supervisionam embalagem e etiquetagem de produtos e planejam atividades de trabalho para a sua realização. Avaliam condições de uso e monitoram a movimentação (fluxo) e a utilização das embalagens. Supervisionam equipes de trabalho e controlam a qualidade de produtos para embalagem. Trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, meio ambiente, higiene e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7811", "Condutores de processos robotizados", "Preparam e programam robôs para operação. Conduzem processos produtivos de operações com robôs; controlam parâmetros de aplicação de materiais e realizam manutenção de rotina em robôs. Elaboram documentação técnica (registro de dados de desempenho das máquinas, fichas de controle, manuais de procedimentos, pareceres técnicos) e trabalham em conformidade com normas e procedimentos técnicos, de segurança, higiene e qualidade.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7813", "Operadores de equipamentos pilotados remotamente", "Operam veículos remotamente controlados (robôs), orientando-se por instrumentos de navegação, para gerar imagens e realizar inspeções e intervenções em estruturas e equipamentos submarinos. Planejam, mobilizam e documentam atividades de operações do sistema rov. Realizam manutenção de equipamentos, diagnosticando defeitos e falhas, reparando e substituindo componentes mecânicos e eletroeletrônicos. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "07/2022A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7817", "Trabalhadores subaquáticos", "Planejam e realizam operações subaquáticas. Inspecionam instalações subaquáticas e realizam manutenção corretiva nas suas estruturas. Prestam socorro, buscam e resgatam objetos, pessoas e corpos submersos. Elaboram documentação de operações subaquáticas. Trabalham seguindo normas de segurança, saúde, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7821", "Operadores de máquinas e equipamentos de elevação", "Operam máquinas e equipamentos de elevação, ajustando comandos, acionando movimentos das máquinas. Avaliam condições de funcionamento das máquinas e equipamentos, interpretando painel de instrumentos de medição, verificando fonte de alimentação, testando comandos de acionamento. Preparam área para operação dos equipamentos e transportam pessoas e materiais em máquinas e equipamentos de elevação. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7822", "Operadores de equipamentos de movimentação de cargas", "Preparam movimentação de carga e a movimentam. Organizam carga, interpretando simbologia das embalagens, armazenando de acordo com o prazo de validade do produto, identificando características da carga para transporte e armazenamento e separando carga não-conforme. Realizam manutenções previstas em equipamentos para movimentação de cargas. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7823", "Motoristas de veículos de pequeno e médio porte", "Dirigem e manobram veículos e transportam pessoas, cargas ou valores. Realizam verificações e manutenções básicas do veículo e utilizam equipamentos e dispositivos especiais tais como sinalização sonora e luminosa, software de navegação e outros. Efetuam pagamentos e recebimentos e, no desempenho das atividades, utilizam-se de capacidades comunicativas. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7824", "Motoristas de ônibus urbanos, metropolitanos e rodoviários", "Conduzem e vistoriam ônibus e trólebus de transporte coletivo de passageiros urbanos, metropolitanos e ônibus rodoviários de longas distâncias; verificam itinerário de viagens; controlam o embarque e desembarque de passageiros e os orientam quanto a tarifas, itinerários, pontos de embarque e desembarque e procedimentos no interior do veículo. Executam procedimentos para garantir segurança e o conforto dos passageiros. Habilitam-se periodicamente para conduzir ônibus.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7825", "Motoristas de veículos de cargas em geral", "Transportam, coletam e entregam cargas em geral; guincham, destombam e removem veículos avariados e prestam socorro mecânico. Movimentam cargas volumosas e pesadas, podem, também, operar equipamentos, realizar inspeções e reparos em veículos, vistoriar cargas, além de verificar documentação de veículos e de cargas. Definem rotas e asseguram a regularidade do transporte. As atividadessão desenvolvidas em conformidade com normas e procedimentos técnicos e de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7826", "Operadores de veículos sobre trilhos e cabos aéreos", "Conduzem e manobram trens, bondes e metrôs - monitorando equipamentos de bordo e movimentando o veículo na modalidade manual, semi-automática e automática - e operam teleféricos para transportar passageiros e cargas, adequando a condução ao tipo de veículo. Realizam inspeções e vistorias nos veículos e tomam providências para corrigir falhas detectadas nos equipamentos. Seguem procedimentos de segurança, obedecendo sinalização de via, acatando instruções enviadas por rádio e acionando freio de e", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7827", "Trabalhadores aquaviários", "Comandam e imediatam pequenas embarcações, auxiliando o comandante na administração de bordo e no serviço de manobras; chefiam praça de máquinas; transportam cargas e passageiros; realizam manobras, serviços e manutenção no convés; operam máquinas; realizam manutenção preventiva e corretiva da praça de máquinas e aplicam procedimentos de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7828", "Condutores de animais e de veículos de tração animal e pedais", "Organizam e conduzem a comitiva e cozinham para seus membros. Transportam e arreiam animais e guiam a boiada. Cuidam dos animais e efetuam manutenção nos veículos. Conduzem veículos (charrete, carroça, bicicleta) e transportam pessoas, mercadorias e materiais. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7831", "Trabalhadores de manobras de transportes sobre trilhos", "Manobram veículos ferroviários e estacionam trens. Acoplam e desaclopam vagões e carros, operam aparelho de mudança de via (amv), revisam veículos ferroviários e controlam pátio de manobras. Preenchem registros burocráticos com informações técnicas e operacionais das atividades realizadas. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7832", "Trabalhadores de cargas e descargas de mercadorias", "Preparam cargas e descargas de mercadorias; movimentam mercadorias em navios, aeronaves, caminhões e vagões; entregam e coletam encomendas ; manuseiam cargas especiais; reparam embalagens danificadas e controlam a qualidade dos serviços prestados. Operam equipamentos de carga e descarga; conectam tubulações às instalaçõesde embarque de cargas; estabelecem comunicação, emitindo, recebendo e verificando mensagens, notificando e solicitando informações, autorizações e orientações de transporte, emb", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7841", "Trabalhadores de embalagem e de etiquetagem", "Preparam máquinas e local de trabalho para empacotar e envasar; embalam produtos e acessórios; enfardam produtos, separando, conferindo, pesando e prensando produtos; realizam pequenos reparos em máquinas, identificando falhas, regulando-as, substituindo pequenas peças e testando seu funcionamento.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7842", "Alimentadores de linhas de produção", "Preparam materiais para alimentação de linhas de produção; organizam a área de serviço; abastecem linhas de produção; alimentam máquinas e separam materiais para reaproveitamento.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("7911", "Artesãos", "Os profissionais desta família ocupacional criam e confeccionam produtos artesanais utilizando-se de vários tipos de matérias primas, tais como: fibras, madeira, pedras, sementes e cascas, tecidos, metais, couro, látex dentre outros. Para tanto, utilizam-se de várias técnicas de tratamento, preparação e tranformação das matérias primas utilizadas. Finalizam seus produtos de modo que os mesmos retratem a cultura local e identifiquem seu autor. São responsáveis pela comercialização de seus produto", "S", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8101", "Supervisores de produção em indústrias químicas, petroquímicas e afins", "Planejam e gerenciam processos contínuos de produção química, petroquímica e afins, corrigindo desvios das condições normais de operação. Supervisionam a elaboração de procedimentos técnicos operacionais e tratam anomalias. Lideram, desenvolvem e avaliam equipes de trabalho e participam na elaboração de documentos normativos (instruções de serviço, manuais de operação e outros). Trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, higiene, saúde e preservação am", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8102", "Supervisores de produção em indústrias de transformação de plásticos e borrachas", "Supervisionam a fabricação de produtos plásticos e de borracha; controlam parâmetros de processos e metas de produção; coordenam equipes de trabalho; controlam cumprimento de normas e procedimentos administrativos de equipes de trabalho; elaboram documentação técnica e aplicam normas e procedimentos de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8103", "Supervisores de produção em indústrias de produtos farmacêuticos, cosméticos e afins", "Planejam atividades de supervisão; supervisionam e otimizam processos de produção de produtos farmacêuticos, cosméticos e afins; supervisionam e treinam equipes de trabalho, assegurando o cumprimento de normas e zelando pela segurança, saúde e meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8110", "Operadores polivalentes de equipamentos em indústrias químicas, petroquímicas e afins", "Preparam passagem de turno e controlam etapas do processo químico e petroquímico. Realizam análises químicas e físicas e zelam pelo funcionamento das instalações e equipamentos. Operam instalações industriais e equipamentos de campo e controlam fluxo de materiais e insumos. Trabalham em conformidade a normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8111", "Operadores de equipamentos de moagem e mistura de materiais (tratamentos químicos e afins)", "Preparammáquinas equipamentos e materiais, como, pastas, bases e concentrados para tintas; moem matérias-primas, tais como dióxidos, óxidos, carbonatos, pigmentos orgânicos e inorgânicos; fabricam e acondicionam tintas e concentrados, resinas e vernizes;aplicam normas e procedimentos de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8112", "Operadores de calcinação e de tratamentos químicos de materiais radioativos", "Preparam interfaces de turno de trabalho e monitoram equipamentos e variáveis do processo de produção. Operam etapas do processo produtivo, realizam manobras de alinhamento dos processos e controlam resultados das etapas do processo produtivo e do sistema de qualidade. Produzem em bateladas (celulose, metais, têxtil e produtos químicos), vazam (descarregam) o forno e treinam novos operadores. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8113", "Operadores de equipamentos de filtragem e separação", "Preparam o local de trabalho e operam filtro-prensa, filtros de secagem, tambor, esteira e centrifugadora; amostram materiais, coletando, identificando e analisando-os, registrando e comparando resultados daanálise;controlam estoque de materiais e equipamentos e aplicam normas e procedimentos de segurança.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8114", "Operadores de equipamentos de destilação, evaporação e reação", "Controlam variáveis de processos de destilação, evaporação e reação, operam aparelhos de destilação, de evaporação e reatores, realizam análises físico-químicas de produtos e mantêm máquinas e equipamentos em condições de uso. No desempenho das atividades utilizam-se de capacidades comunicativas e trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8115", "Operadores de equipamentos de produção e refino de petróleo e gás", "Operam processos de produção e refino de petróleo e gás e suas interfaces de controle, tais como sistemas digitais de controle distribuído (sdcd), painéis e instrumentos, de acordo com normas de segurança, meio ambiente e de saúde ocupacional. Utilizam ferramentas de qualidade para controlar produtos e efluentes. Preparam equipamentos para serviços de manutenção. Elaboram relatórios de ocorrência e participam de reuniões técnicas.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8116", "Operadores de equipamentos de coqueificação", "Preparam e controlam processos de coqueificação, desenfornam o coque e realizam tratamentos primários nos subprodutos do carvão. Efetuam manutenção de fornos e processam subprodutos da coqueificação. Trabalham em conformidade a normas e procedimentos técnicos de qualidade, segurança, higiene, saúde e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8117", "Operadores de instalações e máquinas de produtos plásticos, de borracha e moldadores de parafinas", "Fabricam produtos em plástico e borracha e velas. Preparam matérias-primas, máquinas e moldes para fabricação dos produtos. Controlam e documentam o processo de produção. Trabalham em conformidade a normas e procedimentos técnicos de qualidade, segurança, higiene, saúde e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8118", "Operadores de máquinas e instalações de produtos farmacêuticos, cosméticos e afins", "Preparam a operação de máquinas de produtos farmacêuticos e afins. Fabricam, envasam e embalam produtos farmacêuticos e afins. Realizam manutenção preventiva das máquinas, equipamentos e instalações e preenchem fichas de controle de processo. Trabalham em conformidade a normas e procedimentos de segurança, saúde e higiene, sanificando equipamentos, higienizando instalações, selecionando e acondicionando materiais tóxicos e descartando resíduos.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8121", "Trabalhadores da fabricação de munição e explosivos químicos", "Fabricam munições, explosivos, fogos de artifício e acessórios iniciadores de explosivos tais como, pólvora à base química, pólvora negra, pólvora branca e chumbo, operando máquinas de processamento químico, de usinagem, de montagem e embalagem de produtos. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8131", "Operadores de processos das indústrias de transformação de produtos químicos, petroquímicos e afins", "Realizam interfaces de turnos de trabalho, programam atividades de produção e monitoram funcionamento de equipamentos e sistemas. Controlam parâmetros do processo produtivo, operam suas etapas e movimentam materiais e insumos. Transformam polímeros em produtos intermediários ou finais e realizam manutenção de primeiro nível. Trabalham em conformidade a normas e procedimentos técnicos de qualidade, segurança, higiene, saúde e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8181", "Laboratoristas industriais auxiliares", "Planejam o trabalho de apoio do laboratório e preparam vidrarias e materiais similares. Preparam soluções e equipamentos de medição e ensaios e analisam amostras de insumos e matérias-primas. Organizam o trabalho conforme normas de segurança, saúde ocupacional e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8201", "Supervisores de produção em indústrias siderúrgicas", "Supervisionam equipe de trabalho, controlam processos de produção siderúrgica e administram metas e resultados de produção; controlam recursos para a produção siderúrgica, elaboram documentação técnica e operacional, controlam o cumprimento de normas e procedimentos técnicos de segurança do trabalho, de meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8202", "Supervisores na fabricação de materiais para construção (vidros e cerâmicas)", "Controlam matérias-primas e processos produtivos da fabricação de materiais para construção (vidros e cerâmicas). Implementam sistemas de qualidade e de preservação do meio ambiente no processo de fabricação de produtos para construção. Administram custos e orçamentos, controlam estoques de produtos acabados e gerenciam equipes de trabalho. Organizam o trabalho conforme normas de segurança, saúde ocupacional e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8211", "Operadores de instalações de sinterização", "Operam centros de controle,máquinas de sinterização, equipamentos de recebimento, transporte, silagem e dosagem de matérias- primas; preparam amostras para análises físico-químicas e metalúrgicas; beneficiam matérias-primas para sinterização e alto-forno; controlam a qualidade de matérias-primas e produtos. Organizam o desenvolvimento das atividades e cumprem programasde segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8212", "Operadores de fornos de primeira fusão e aciaria", "Preparam máquinas, equipamentos e materiais, operam alto-forno, vazam e dessulfuram ferro gusa, realizam manutenção refratária e controlam características físico-químicas dos produtos e das matérias-primas. Produzem e vazam metal líquido e realizam tratamentos secundários nos metais. Trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, higiene, saúde e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8213", "Operadores de equipamentos de laminação", "Preparam processos de laminação de metais, laminam barras e tubos, a quente e a frio, recuperam guias, montam cilindros e mancais, em conformidade a normas e procedimentos técnicos, de segurança, meio ambiente e saúde.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8214", "Operadores de equipamentos de acabamento de chapas e metais", "Preparam acabamento de materiais metálicos, realizam tratamento térmico em chapas e metais e controlam a qualidade dos produtos. Identificam e bobinam produtos metálicos e controlam o fluxo e o processo de acabamento. Laminam tarugos e tiras de aço e preparam sucata e escória. Trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, higiene, saúde e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8221", "Forneiros metalúrgicos (segunda fusão e reaquecimento)", "Realizam fundição e tratamento térmico de metais e ligas e preparam fornos para operação, carregando-os com materiais. Ajustam a composição química de ligas metálicas, realizam vazamento de metal e preparam fornos para manutenção. Registram as ocorrências técnicas e operacionais e trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, higiene, saúde e preservação ambiental.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8231", "Operadores na preparação de massas para abrasivo, vidro, cerâmica, porcelana e materiais de construç", "Preparam máquinas e equipamentos,aditivos, barbotina, massa cerâmica e de vidro, esmaltes e tintas cerâmicas. Produzem ligas aglomerantes e misturas abrasivas e monitoram a produção de aditivos, barbotinas, esmaltes, tintas, massa cerâmica, massa de vidro e abrasivos. Trabalham seguindo normas de qualidade, segurança, higiene, saúde e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8232", "Operadores de equipamentos de fabricaçãoe beneficiamento de cristais, vidros, cerâmicas, porcelanas,", "Preparam e operam fornos para processamento de minerais não-metálicos. Preparam máquinas, equipamentos e insumos de conformação dos produtos. Controlam processos de produção e especificações do produto e do processo. Informam e registram ocorrências setoriais tais como parada de máquinas, parâmetros do processo, preenchimento de ordens de serviços e outras. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8233", "Operadores de instalações e equipamentos de fabricação de materiais de construção", "Preparam moldes, fôrmas e pisos, massas para fabricação de peças de concreto armado, cimento amianto e pedras artificiais. Moldam, realizam acabamento, classificam e acondicionam peças de concreto armado, pedras artificiais, cimento amianto e tijolos refratários. Preparam fornos e realizam a queima de peças de cerâmica vermelha, tijolos, telhas e pisos. Trabalham seguindo normas de segurança, higiene, qualidade e preservação ambiental.", "N", "202308", "01/2017A", "2");
