INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("8483", "Padeiros, confeiteiros e afins", "Planejam a produção e preparam massas de pão, macarrão e similares. Fazem paes, bolachas e biscoitos e fabricam macarrão. Elaboram caldas de sorvete e produzem compotas. Confeitam doces, preparam recheios e confeccionam salgados. Redigem documentos tais como requisição de materiais registros de saída de materiais e relatórios de produção. Trabalham em conformidade a normas e procedimentos técnicos e de qualidade, segurança, higiene, saúde e preservação ambiental.", "N", "202308", "01/2017A", "2");
