INSERT INTO TB_FAMILIA_CBO (CO_FAMILIA_CBO,NO_FAMILIA_CBO,DS_FAMILIA_CBO,ST_ASSOCIAVEL_PROCEDIMENTO,DT_COMPETENCIA,CO_VERSAO,ST_ACAO) VALUES ("9913", "Reparadores de carrocerias de veículos", "Analisam o veículo a ser reparado, realizam o desmonte e providenciam materiais, equipamentos, ferramentas e condições necessárias para o serviço. Preparam a lataria do veículo e as peças para os serviços de lanternagem e pintura. Confeccionam peças simples para pequenos reparos. Pintam e montam o veículo. Trabalham seguindo normas de segurança, higiene, qualidade e proteção ao meio ambiente.", "N", "202308", "01/2017A", "2");
